// golfed version by FabriceNeyret2
void mainImage(out vec4 O, vec2 u){
    float i,e,R,s, t=iTime;
    vec3 q,p,d = vec3( u/iResolution.xy +vec2(-.5,.4), 1);
    O *= 0.;

    for( q.zy-- ; i++ < 79. ; ){
        O += .02 - .05/exp(e*9e2) * vec4(d.y,i,.8,0);
        p = q += d*e*R*.06;
        p = vec3( log2( R = length(p) ) -t*3.,
                  exp( R - p.z/R ),
                  t - atan(p.x,p.y)*3.
                );
        for( s=2., e = --p.y ; s < 8e2; s+=s )
            e += dot( sin(p.zy*s), cos(p.xx*s+t*3.) ) / s;
    }
}
