void mainImage( out vec4 Q, in vec2 U)
{
    U = 2.1*(U-.5*R)/R.y;
    vec2 U0 = U;
        U.y -= 1.0;
        U /= dot(U,U);
        U.y = -0.5 - U.y;
        U /= dot(U,U);
     U.x -= .3*iTime;
    float theta = pi/7.0;
    float r = 2.0 / (1.0 - sqrt(1.0 - 4.0 * sin(theta) * sin(theta)));
    float p = - r * cos(theta);
    for(int i=0;i<100;i++) {
        if (U.x < 0.0) {
            U.x = -U.x;
            continue;
        }
        if (dot(U,U) < 1.0) {
            U /= dot(U,U);
            continue;
        }
        U.x -= p;
        if (dot(U,U) > r*r) {
            U *= r * r / dot(U,U);
            U.x += p;
            continue;
        }
        break;


    }
        U.x = 2.5*(U.x-3.25-.25*cos(.1*iTime));
        U.y = U.y-1.;

        Q = vec4((sqrt(1.-dot(U0,U0)))*pow(abs(U.y),.5)*sqrt(1.-.3*U.x*U.x));
        Q = clamp(.8*Q,0.,1.);


}

float map (vec3 p) {
    vec2 u = (p.xy*.5*R.y+.5*R);
    return abs(p.z+.5*A(u).x+.3)-.1;
}
vec3 normal (vec3 p )  {
    vec2 u = (p.xy*.5*R.y+.5*R);
    float n = A(u+vec2(0,1)).x;
    float e = A(u+vec2(1,0)).x;
    float s = A(u-vec2(0,1)).x;
    float w = A(u-vec2(1,0)).x;
    return normalize(vec3(e-s,n-s,10./R.y));
}
void mainImage( out vec4 Q, in vec2 U )
{
    vec3 p = vec3(0,0,-2.);
    vec2 h = .5*(hash(vec4(U,iFrame,iTime)).xy*2.-1.);
    vec3 d = normalize(vec3(2.*(U+h-.5*R)/R.y,2));

    p.yz *= ei(0.6);
    d.yz *= ei(0.6);

    for (float i = 0.; i < 300.; i++) {
        float m = .5*map(p);
        p += .8*d*m;
        if (m < 1e-5) {
            d = reflect(d,normal(p));
            p += .01*d;
        }
    }
    Q = B(U)*.9+.1*vec4(abs(d),1)*texture(iChannel2,d);
}

#define R iResolution.xy
#define pi 3.14159265359
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
