void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;

    // Calculate the modulation value for the wave effect
    float waveModulation = 0.5 + 0.5 * sin(iTime);

    // Calculate spherical lighting effect
    float lightingEffect = abs(sin(iTime - uv.y)) / (length(uv) + 0.0001) * 0.3;

    // Apply modulation to the lighting effect
    lightingEffect *= waveModulation;

    vec3 color = lightingEffect * mix(vec3(0.2, 0.7, 0.4), vec3(0.2, 0.4, 1.0), abs(uv.x));

    // Output to screen
    fragColor = vec4(color, 1.0);
}
