#define PI2 6.28318530718

#define time iTime
#define resolution iResolution.xy


uniform vec2 mouse;


float getPhase(float t, float spd, float phase){
	return min(1.0,max(0.0,cos(time*spd+phase*PI2)*1.2));
}

float getSpike(vec2 p, float spikes, float t){
	float a = atan(p.y,p.x);
	return cos(a*spikes+t)*0.5+0.5;
}
const float PI = 3.1415927;

const float n = 8.;
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 position = (( gl_FragCoord.xy / resolution.xy ) -vec2(0.5,0.5))*vec2(resolution.x/resolution.y,1.0)*2.0;
	vec2 center = vec2(0.0,0.0);

	vec2 p = position;
	float t=time;
	vec2 rc = (gl_FragCoord.xy - resolution.xy * 0.5) / min(resolution.x, resolution.y);
	float a = 0.;

	for(float i = 0.; i < n; i++) {
		float angle = time + i / n * PI2;
		vec2 p = vec2(cos(angle), sin(angle)) * (0.35 + tan(angle * 000.5) * 0.13);
		a += smoothstep(1., 0., distance(rc, p) /0.11);

	}
	a += smoothstep(01.5, 0., abs(0.25 - length(rc)) / 0.001);
	a += smoothstep(0., 0., abs(0.15 - length(rc)) / 0.001);

	float spikes = 1.0;
	float d = length(center-position);

	float spikeMod = 0.0;
	spikeMod += getSpike(p, 5.0, t*0.7)*(cos(t*0.4)*0.5+0.7)*0.1;
	spikeMod += getSpike(p, 3.0, t*0.3)*(cos(t*0.7)*0.5+0.7)*0.1;
	spikeMod += getSpike(p, 2.0, t*0.4)*(cos(t*1.4)*0.5+0.7)*0.1;
	spikeMod += getSpike(p, 12.0, t*5.1)*0.01;
	spikeMod += getSpike(p, 25.0, -t*3.2)*0.02;
	spikeMod += getSpike(p, 37.0, -t*2.3)*0.03;
	float modulator = 1.0;

	float light = pow((0.02+spikeMod*a)/d,modulator);

	float spd = 1.0;

	vec3 clr = vec3(getPhase(time, spd,0.1), getPhase(time, spd,1.0/2.0),getPhase(time, spd,1.0/3.0));
	clr = vec3(0.006)+clr*0.013+a;

	vec3 color = vec3(light*clr.r*a, light*clr.g, light*clr.b+a);

	if(position.x>1.0)color=vec3(0.0);
	if(position.x<-1.0)color=vec3(0.0);
	if(position.y>1.0)color=vec3(0.0);
	if(position.y<-1.0)color=vec3(0.0);

	fragColor = vec4( color, 1.0 );
;
}
