// Public Domain under http://unlicense.org, see link for details.

// Visualize the accuracy of HW sampling. It may
// be relevant for many things, including e.g.
// https://www.shadertoy.com/view/lflSWn.

vec4 sampleHW(vec2 uv)
{
    return texture(iChannel0,uv);
}

vec4 sampleSW(vec2 uv)
{
    // WARNING: does no wrapping at all.
    uv=uv*vec2(textureSize(iChannel0,0))-0.5;
    ivec2 c=ivec2(uv),e=ivec2(0,1);
    uv=uv-vec2(c);
    return mix(
        mix(texelFetch(iChannel0,c+e.xx,0),texelFetch(iChannel0,c+e.yx,0),uv.x),
        mix(texelFetch(iChannel0,c+e.xy,0),texelFetch(iChannel0,c+e.yy,0),uv.x),
        uv.y);
}

float plot(float t) {return smoothstep(2.5 /* *fwidth(t) */,0.0,abs(t));}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    const float SCALE=64.0,S=32.0;
    vec2 m=1.0/(SCALE*vec2(textureSize(iChannel0,0)));
    vec2 c=fragCoord;
    vec3 col=sampleHW(m*c).rgb;
    vec2 A=abs(iMouse.zw),B=abs(iMouse.xy);
    col=mix(col,vec3(1),smoothstep(8.0,4.0,length(A-c)));
    col=mix(col,vec3(1),smoothstep(8.0,4.0,length(B-c)));
    col=mix(col,vec3(1),smoothstep(2.0,1.0,max(length(2.0*c-A-B)-length(B-A),0.0)+abs(dot(vec2(c.y-A.y,A.x-c.x),normalize(B-A)))));
    vec4 hw=sampleHW(m*mix(A,B,c.x/iResolution.x));
    vec4 sw=sampleSW(m*mix(A,B,c.x/iResolution.x));
    col+=vec3(1,0,0)*plot((S*(hw-sw)+0.5).x*iResolution.y-c.y);
    col+=vec3(0,1,0)*plot((S*(hw-sw)+0.5).y*iResolution.y-c.y);
    col+=vec3(0,0,1)*plot((S*(hw-sw)+0.5).z*iResolution.y-c.y);
    vec2 grid=fract(c/SCALE-0.5);
    grid=min(grid,1.0-grid);
    col*=0.5+0.5*smoothstep(0.0,1.0/SCALE,min(grid.x,grid.y));
    fragColor=vec4(col,1.0);
}
