void mainImage( out vec4 C, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    vec4 a = texture(iChannel0,u);
    C = .3*a.z*vec4(1,1.2,2,1)+.1*a.z*length(a.xy)*vec4(1,1.4,1,1);
    C -= f(fragCoord)*(.4- .4*dgx(fragCoord))*vec4(1);    
    C += (.3+ .02*dgx(fragCoord))*vec4(1);

}

#define A(u) texture(iChannel0,(u)/iResolution.xy)

#define ks 4.0   //kernel convolution size
#define kf 0.7   //kernel factor : 1/(2*sigmaÂ²)
#define gf 0.03  //gravity force
#define af .7    //attraction force 

float h(vec2 u){
   return (5.*sin(.032*u)*sin(.022*u.yx)+6.*cos(.046*u.yx+.7)*cos(.026*u)).y;
}

float g(vec2 u){
   return h(u) + .5*h(2.*u.yx)+ .3*h(3.*u+1.);
}

float f(vec2 u){
   return clamp(exp(g(u))-15.,0.,1.);
}



float dgx(vec2 u){
   return g(u+vec2(1,0))-g(u-vec2(1,0));
}

void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4  o = vec4(0);   
    for(float i=-ks; i<=ks; i++){
    for(float j=-ks; j<=ks; j++){
      vec2 d = vec2(i,j);
      vec2 v = A(u+d).xy-vec2(0,gf);       
      o += A(u+d).z                                // neighbour mass
         * exp(-dot(v+d,v+d)*kf) / 3.1416 *kf      // normalised Gaussian
         * vec4(v + af*(1.- A(u+d).z- 5.*f(u+d))*(v+d),1,0);    // velocity contribution      
    }}

    if(o.z!=0.){o.xy /= o.z;} //calculate the average velocity        
    float l = length(o.xy);   //clamp velocity to avoid mass leakage
    if (l>2.)o.xy /=l/2.;
     // Boundaries    
    vec2 p = u/iResolution.xy;
    if(p.x>.98||p.x<.02||p.y>.98){o.z=0.;}

    if(iMouse.z>0.)                       //mouse click adds velocity
    {
        vec2 m = 8.*(u-iMouse.xy)/iResolution.y;
        o += vec4(m,.1,0)*exp(-dot(m,m));
    }

    vec2 m = .03*(u-iResolution.xy*vec2(.5,.9));
        o += vec4(0,0,.04,1)*exp(-dot(m,m));
    fragColor = o;
}
