vec4 noised( in vec3 x )
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);
    vec3 du = 30.0*w*w*(w*(w-2.0)+1.0);

    // gradients
    vec3 ga = hash( p+vec3(0.0,0.0,0.0) );
    vec3 gb = hash( p+vec3(1.0,0.0,0.0) );
    vec3 gc = hash( p+vec3(0.0,1.0,0.0) );
    vec3 gd = hash( p+vec3(1.0,1.0,0.0) );
    vec3 ge = hash( p+vec3(0.0,0.0,1.0) );
    vec3 gf = hash( p+vec3(1.0,0.0,1.0) );
    vec3 gg = hash( p+vec3(0.0,1.0,1.0) );
    vec3 gh = hash( p+vec3(1.0,1.0,1.0) );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolation
    float v = va + 
              u.x*(vb-va) + 
              u.y*(vc-va) + 
              u.z*(ve-va) + 
              u.x*u.y*(va-vb-vc+vd) + 
              u.y*u.z*(va-vc-ve+vg) + 
              u.z*u.x*(va-vb-ve+vf) + 
              u.x*u.y*u.z*(-va+vb+vc-vd+ve-vf-vg+vh);

    vec3 d = ga + 
             u.x*(gb-ga) + 
             u.y*(gc-ga) + 
             u.z*(ge-ga) + 
             u.x*u.y*(ga-gb-gc+gd) + 
             u.y*u.z*(ga-gc-ge+gg) + 
             u.z*u.x*(ga-gb-ge+gf) + 
             u.x*u.y*u.z*(-ga+gb+gc-gd+ge-gf-gg+gh) +   

             du * (vec3(vb-va,vc-va,ve-va) + 
                   u.yzx*vec3(va-vb-vc+vd,va-vc-ve+vg,va-vb-ve+vf) + 
                   u.zxy*vec3(va-vb-ve+vf,va-vb-vc+vd,va-vc-ve+vg) + 
                   u.yzx*u.zxy*(-va+vb+vc-vd+ve-vf-vg+vh) );

    return vec4( v, d.x, d.y, d.z );                   
}

float casin(float x) {
	float negate = float(x < 0.0);
	x = abs(x);
	float ret = -0.0187293;
	ret *= x;
	ret += 0.0742610;
	ret *= x;
	ret -= 0.2121144;
	ret *= x;
	ret += 1.5707288;
	ret = 3.14159265358979*0.5 - sqrt(1.0 - x)*ret;
	return ret - 2.0 * negate * ret;
}

float atan2(float y, float x) {
 	float absx, absy, val;

     if (x == 0.0 && y == 0.0) {
 		return 0.0;
 	}
 	absy = y < 0.0 ? -y : y;
 	absx = x < 0.0 ? -x : x;
 	if (absy - absx == absy) {
 		return y < 0.0 ? -3.1415*2.0 : 3.1415*2.0;
 	}

    if (absx - absy == absx) {
		val = 0.0;
    } else {
        val = casin((y/x)/sqrt(1.0+((y/x)*(y/x))));
    }
 	if (x > 0.0) {
 		return val;
 	}
 	if (y < 0.0) {
		return val - 3.1415;
 	}
 	return val + 3.1415;
}

vec4 noise2( in vec3 x ){

    return 
    noised(x*2.0)*(1.0/2.0)+
    noised(x*4.0)*(1.0/4.0)+
    noised(x*8.0)*(1.0/8.0)+
    noised(x*16.0)*(1.0/16.0)+
    noised(x*32.0)*(1.0/28.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/max(iResolution.x,iResolution.y);

    float zoom = (34.0*sin(iTime/10.));

    float tiles = 40.0-zoom;

    vec2 c_uv = (uv-vec2(0.5, 0.5));

    vec2 box = fract(c_uv*tiles);
    vec2 grad = noise2(0.3*vec3(floor(c_uv*tiles)/tiles, iTime/3.)).yz;
    float angle = atan2(grad.y, grad.x) + 3.14159/2.0;


    vec2 P = vec2(0.5, 0.5);
    float dist = abs(cos(angle)*(P.y-box.y)-sin(angle)*(P.x-box.x));

    vec3 col = vec3(1.0-(100.*dist*dist));
    // Output to screen
    fragColor = vec4((col), 1.0);
}
