void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float d1 = length(uv);


    int i=1;
    int x=               1000;  //Change to adjust the number of orbs
    float speed =        5000.0;  //Change to adjust speed 
    float orbgradient =   1.0;  //Change to make the color offset between outer and inner orbs bigger
    float orbgrade =      2.5;  //Change to make the color offset between first and last orbs bigger
    float orbdarkening =  1.0;  //Change to adjust the darkening of the outer Orbs
    float orb3d =         1.3;  //Change to adjust the 3d Coloring 
    float orbbase =       1.0;  //Change to adjust the base size of the orbs 
    float orbgrowth =     1.0;  //Change to adjust the size of the orb growth
    float bggradient =    4.5;  //Change to adjust the background gradient
    float size =          0.8;  //Change to adjust the size of the first cirlce/Oscillation amplitue
    float circlesize =    0.8;  //Change to adjust the size of the second circle/Oscillation amplitue
    speed = 100./speed;
    float x1=float(x);
    uv *= bggradient;
    float d = length(uv);
    vec3 f = 0.55 + 0.3*cos(iTime*0.0025+vec3(d)+vec3(0,2,4));

    for(i=1; i < x; i++){
        uv = fragCoord/iResolution.xy;
        uv -= 0.5;
        uv.x *= iResolution.x/iResolution.y;
        uv -= vec2(cos(iTime * float(i)/x1)*0.5*size, sin(iTime * float(i)/x1)*0.5*size);
        uv += vec2(sin(iTime + float(i)/speed)*0.5*circlesize, cos(iTime + float(i)/speed)*0.5*circlesize);
        d = length(uv);
        if(d < (.05*orbbase + sin(iTime*0.25 + float(i)/x1)*0.025)*.5*orbgrowth){ 
            f = 0.6 + 0.3*sin(iTime*0.025+vec3(d1)*orbgradient+vec3(3,5,7)+float(i)/x1*orbgrade);
            f -= vec3(d1)*0.055*orbdarkening; 
            f += vec3(d + uv.x + uv.y)*orb3d; 
                 }
    } 
    // Output to screen
    fragColor = vec4(f,1.0);
}
