/*
by Dom Mandy in 2024
*/
void mainImage(out vec4 P, vec2 V) {
    vec2 Z = iResolution.xy,
         C = V = 2.1 * (Z - V - V).yx / Z.x,
         // inverse coordinate for root
         A = vec2(1, 0);
    A = vec2(dot(A, C), dot(vec2(A.y, -A.x), C)) / dot(C, C);

    float v, x, y,
          z = y = x = 9.;

    for (int k; k < 666; k++) {
        // addition
        V += C;
        // root
        float a = atan(V.y, V.x), 
              d = dot(V, V),
              c = dot(A, vec2(a, log(d) / 2.));
        V = exp(-a * A.y) * pow(d, A.x / 2.) * vec2(cos(c), sin(c));
        // division
        V = vec2(dot(V, C), dot(vec2(V.y, -V.x), C)) / dot(C, C);

        // orbits
        x = min(x, abs(V.x));
        y = min(y, abs(V.y));
        z > (v = dot(V, V)) ? z = v, Z = V : Z;
    }

    float l = 9.;
    l = smoothstep(1., -l, log(x)) * smoothstep(1., -l, log(y));

    P.rgb = sqrt(l + (l - l * l * l) * cos(atan(Z.y, Z.x) - vec3(0, 2.1, 4.2)));
}
