float ReadKey      (int keyCode) {return texelFetch(iChannel1, ivec2(keyCode, 0), 0).x;}
float ReadKeyToggle(int keyCode) {return texelFetch(iChannel1, ivec2(keyCode, 2), 0).x;}

float ReadVar(int cx, int cy) {return texelFetch(iChannel0, ivec2(cx, cy), 0).w;}
vec4 ReadVar4(int cx, int cy) {return vec4(ReadVar(cx, cy), ReadVar(cx, cy + 1), ReadVar(cx, cy + 2), ReadVar(cx, cy + 3));}

float ReadVar(inout int x) { return ReadVar(x++, 0); }
vec4 ReadVar4(inout int x) { return ReadVar4(x++, 0); }

vec4 AA[8], BB[8], CC[8], DD[8];
vec4 A0, B0, C0, D0;
vec4 A1, B1, C1, D1;

bool ImplicitRC(vec3, vec3, float, float, float, float, float, out float, out float, out float);
bool ImplicitRC(vec3, vec3, float, float, float, float, float, out float, out float, out float, inout vec3);

