vec3 TrigNoise3(vec3 x)
{   
    vec4 u = vec4(dot(x, vec3( 1.0, 1.0, 1.0)), 
                  dot(x, vec3( 1.0,-1.0,-1.0)), 
                  dot(x, vec3(-1.0, 1.0,-1.0)),
                  dot(x, vec3(-1.0,-1.0, 1.0)));

    vec4 su = sin(u);
    vec4 cu = cos(u);

    return vec3(dot(cu.xz, su.yw),
                dot(cu.xy, su.zw),
                dot(cu.xy, su.wz));
}

float TrigNoise(vec3 x, float detailScale, float detailAmount)
{   
    vec3 xd = x * detailScale;

    vec4 u = vec4(dot(xd, vec3( 1.0, 1.0, 1.0)), 
                  dot(xd, vec3( 1.0,-1.0,-1.0)), 
                  dot(xd, vec3(-1.0, 1.0,-1.0)),
                  dot(xd, vec3(-1.0,-1.0, 1.0)));

    vec4 su = sin(u);
    vec4 cu = cos(u);

    x.x += dot(cu.xz, su.yw) * detailAmount;
    x.y += dot(cu.xy, su.zw) * detailAmount;
    x.z += dot(cu.xy, su.wz) * detailAmount;

    return dot(sin(x.xyz), 
               cos(x.zxy));
}

float TrigNoise(vec3 x)
{
    return TrigNoise(x, 1.16, 1.33);
}    

// looks bad in xz plane but quite good otherwise
float TrigNoiseXZ(vec3 x, float detailScale, float detailAmount)
{
    vec3 xd = x * detailScale;

    vec4 u = vec4(dot(xd, vec3( 1.0, 1.0, 1.0)), 
                  dot(xd, vec3( 1.0,-1.0,-1.0)), 
                  dot(xd, vec3(-1.0, 1.0,-1.0)),
                  dot(xd, vec3(-1.0,-1.0, 1.0)));

    vec4 su = sin(u);
    vec4 cu = cos(u);

    return dot(sin(x.xyz + cu.xyz * detailAmount), 
               cos(x.zxy + su.zwx * detailAmount));
}

float TrigNoiseXZ(vec3 x)
{
    return TrigNoiseXZ(x, 1.48, 1.44);
}    

