float sdBoxE(vec3 p, vec3 b, float r, float e)
{
    b -= r;

    vec3 d = abs(p)-b;
    vec3 x = max(d, 0.0);

    float u = pow(dot(pow(abs(x), vec3(e)), vec3(1.0)), 1.0/e);

    return u + min(max(d.x, max(d.y, d.z)), 0.0) - r;
}

float Map(vec3 x)
{
    {
        float a = ActiveKnobMatId == 0 ? A0.w : A1.w;
        float b = ActiveKnobMatId == 0 ? B0.w : B1.w;

        x += TrigNoise3(x * (b*12.0)) * (a*0.125);
    }

    float f0;
    if(ReadKeyToggle(KEY_SPACE) == 0.0)
    {
        f0 = length(x) - 0.5;
    }
    else
    {
        float e = 8.0;
        f0 = sdBoxE(x, vec3(0.51), 0.125, 4.0);
    }


    float f = f0;

    if(ActiveKnobMatId == 0)
    {
        x *= B0.x * 80.0;

        f += TrigNoise(x, B0.y*2.0, A0.y*4.0) * 0.008*A0.x;
    }
    else
    {
        x *= B1.x * 80.0;

        f += TrigNoiseXZ(x, B1.y*2.0, A1.y*4.0) * 0.008*A1.x;
    }

    return f;
}



float Intersect_Scene(
vec3 rp, vec3 rd, bool shadowRay,
out float t, out vec3 n, out vec3 a)
{
    float hit = 0.0;
    t = 1e10;
    a = vec3(0.0);

    vec2 tt; vec3 n0, n1;
    float th = Intersect_Ray_Cube(rp, rd, vec3(0.0, 0.0, 0.0), vec3(0.55), tt, n0, n1);

    if(th > 0.0)
    if(hit <= 0.0 || tt.x < t)
    {   
        bool inside = tt.y < 0.0;

        vec3 p = inside ? rp : rp + rd * tt.x;

        float intervalLen = inside ? tt.x : tt.y - tt.x;
        float stepCount = 32.0;
        float minBinaryCount = shadowRay ? 0.0 : 4.0;
        float sdfBlendF = 0.;
        float minStepF = 0.;

        float t0, tm, t1;

        vec3 N = inside ? -rd : n0;
        bool th2 = ImplicitRC(p, rd, intervalLen, stepCount, minBinaryCount, sdfBlendF, minStepF, /*out*/ t0, tm, t1, N);

        if(th2)
        {
            hit = 1.0;

            t = (inside ? 0.0 : tt.x) + tm;

            vec3 p = rp + rd * t;

            n = normalize(N);

            a = vec3(1.0); 

            if(t0 == 0.0) a = vec3(0.5);
        }
    }

    return hit;
}


vec3 EvalSceneCol(vec3 rp, vec3 rd, mat3 cmat, vec3 light)
{
    vec3 col = vec3(0.0119);

    #if 1
    {
        bvec2 a = bvec2(rd.y > 0.0, abs(rd.y) > 0.8);

        bool b = (rd.x > 0.0) != (rd.z > 0.0); 

        col = b != a.y ? col * (a.x ? 1.2 : 0.6) : col * (a.x ? 1.5 : 0.7);
    }
    #endif

    #if 1
    if(ReadKeyToggle(KEY_Q) != 0.0)
    {
        float s = 0.25;
        float r = 0.96;
        col = mix(col, vec3(1.0, rd.x < 0.0 ? 0.125 : 0.0, 0.0), abs(rd.x) < r ? 0.0 : s);
        col = mix(col, vec3(0.0, 1.0, rd.y < 0.0 ? 0.500 : 0.0), abs(rd.y) < r ? 0.0 : s);
        col = mix(col, vec3(rd.z < 0.0 ? 0.250 : 0.0, 0.0, 1.0), abs(rd.z) < r ? 0.0 : s);
    }
    #endif

    float t; vec3 n; vec3 a;
    float hit = Intersect_Scene(rp, rd, false, t, n, a);

    if(hit > 0.0)
    {
        vec3 p = rp + rd * t;

        col = EvalWaxMtl(-rd * cmat, n * cmat);

        if(a.r == 0.5) col = col.gbr;
    }

    return col;
}


void mainImage( out vec4 outCol, in vec2 uv0 )
{
    Resolution = iResolution;

    vec3 col = vec3(0.0);
    vec2 uv = uv0.xy - 0.5;
    ivec2 uvi = ivec2(uv);

    int I = 0;
    vec4 iMouse     = ReadVar4(I);
    vec4 mouseAccu  = ReadVar4(I);
    vec4 wasdAccu   = ReadVar4(I);
    float frameAccu = ReadVar (I);
    ActiveKnobMatId = int(ReadVar(I));

    for(int i = 0; i < KnobMatCount; ++i)
    {
        int KnobMatId = i;
        ivec2 KnobMatStateBuffOrigin = ivec2(KnobMatId * 4, KnobMatStateBuffY0);

        #define READ(xy) texelFetch(iChannel0, xy + KnobMatStateBuffOrigin, 0).w;

        for(int j = 0; j < 4; ++j)
        {
            AA[i][j] = READ(ivec2(j, 0));
            BB[i][j] = READ(ivec2(j, 1));
            CC[i][j] = READ(ivec2(j, 2));
            DD[i][j] = READ(ivec2(j, 3));
        }

        #undef READ
    }

    A0 = AA[0]; B0 = BB[0]; C0 = CC[0]; D0 = DD[0];
    A1 = AA[1]; B1 = BB[1]; C1 = CC[1]; D1 = DD[1];

    vec2 lightAng = vec2(Pi * 0.7, Pi * 0.2);
    lightAng.x += (wasdAccu.y - wasdAccu.w) * 0.06; 
    lightAng.y += (wasdAccu.x - wasdAccu.z) * 0.04;    

    vec3 light = AngToVec(lightAng);

    vec2 ang = vec2(Pi * -0.5, -Pi * 0.2);
    ang += mouseAccu.xy * 0.008;

    mat3 cmat;
    {
        float sinPhi   = sin(ang.x);
        float cosPhi   = cos(ang.x);
        float sinTheta = sin(ang.y);
        float cosTheta = cos(ang.y);    

        vec3 front = vec3(cosPhi * cosTheta, 
                                   sinTheta, 
                          sinPhi * cosTheta);

        vec3 right = vec3(-sinPhi, 0.0, cosPhi);

        vec3 up    = vec3(-cosPhi * sinTheta,
                                    cosTheta,
                          -sinPhi * sinTheta);

        cmat = mat3(right, up, front);
    }

    float cdist = exp2(0.2 + mouseAccu.w * 0.02);
    vec3 cpos = -cmat[2] * cdist;

    float focalLen = 0.7;// = 0.5 * tan(Pi05 - fov * 0.5)

    float txlSize = 1.0 / iResolution.x * 2.0;
    vec2 tc = uv0.xy * txlSize - vec2(1.0, iResolution.y / iResolution.x);

   #if 0

    vec3 rd = normalize(cmat * vec3(tc, focalLen)); 

    col = EvalSceneCol(cpos, rd, cmat, light);  

   #elif 1

    //vec2 flip = vec2(1.0, (fract(uv.x * 0.5) * 4.0 - 1.0) * (fract(uv.y * 0.5) * 4.0 - 1.0));
    vec2 flip = fract(uv.yx * 0.5) * 4.0 - 1.0;
    vec3 off = vec3(2.0, 0.0, -2.0) / 6.0;

    for(uint i = 0u; i < 3u; ++i)
   	{
        vec3 rd = normalize(cmat * vec3(tc + off.xy*flip * txlSize, focalLen)); 

        off = off.zxy;

     	col += EvalSceneCol(cpos, rd, cmat, light);  
    }

    col /= 3.0;

   #elif 1

    vec2 off0 = vec2(0.0);
    vec4 off = vec4(-2.0, -4.0, 4.0, 2.0) * 0.1;

    for(uint i = 0u; i < 5u; ++i)
   	{
    	vec3 rd = normalize(cmat * vec3(tc + off0 * txlSize, focalLen)); 

     	col += EvalSceneCol(cpos, rd, cmat, light); 

        off = off.zxwy;
        off0 = off.xy;
    }

    col *= 0.2;

   #endif

   #if 1
    if(ReadKeyToggle(KEY_TAB) == 0.0 && (IsKnobUITexel(uv) || IsNumValueUITexel(uv)))
    {    
        col = mix(col, col * 0.125 + 0.004, GetKnobUIMask(uv));

        // knobs
        vec3 ui = texelFetch(iChannel0, ivec2(uv), 0).rgb;

        vec3 kcol = vec3(1.0);
        if(ui.z <  0.0) kcol = vec3(0.0, 0.0, 0.0); else
        if(ui.z == 1.0) kcol = vec3(1.0, 0.0, 0.0); else
        if(ui.z == 2.0) kcol = vec3(0.0, 0.5, 0.0); else
        if(ui.z == 3.0) kcol = vec3(0.0, 0.0, 1.5); else
        if(ui.z == 4.0) kcol = vec3(0.9, 0.9, 0.0); else
        if(ui.z == 5.0) kcol = vec3(0.0, 0.8, 0.8); else
        if(ui.z == 6.0) kcol = vec3(0.8, 0.0, 0.8);

       #if 0
        col = mix(col, kcol, ui.y);
        col = mix(col, vec3(0.99), ui.x);
       #else
        col = mix(col, vec3(0.0), ui.y);
        col = mix(col, mix(kcol, vec3(1.0), 0.25)*0.8, ui.x);
       #endif
    }
   #endif    

    #if 1
    {
        vec2 s = abs(uv0/iResolution.xy*2.0-1.0);
        s *= s; s *= s; s = 1.0 - s;
        float u = 1.0 - sqrt(s.x*s.y);
        col *= mix(1.0, 0.75, u*u);
    }
    #endif

    #if 1
    {
        float r2 = fract(dot(uv, vec2(0.7548776662, 0.56984029)));
        r2 = abs(r2 * 2.0 - 1.0);
        col += (r2 - 0.5) * 0.001;
    }
    #endif

	outCol = vec4(sRGB_Encode(clamp01(col)), 1.0);
    //outCol = vec4(col, 1.0);
}




// ========================================================= RAY MARCHING ROUTINES =====================================================================

bool ImplicitRC(
    vec3 x,
    vec3 dir,
    float intervalLen,

    float stepCount,
    float minBinaryCount,
    float sdfBlendF,
    float minStepF,

    out float t0, out float tm, out float t1)
{
    bool hit = false;

    float stepSize = 1.0 / stepCount;
    float ts = intervalLen * stepSize;

    t0 = tm = t1 = 0.0;

    float f  = 0.0;
    float f0 = 0.0;
    float f1 = 0.0;

    for (float i = 0.0; i <= stepCount; ++i)
    {
        tm = hit ? (t0 + t1) * 0.5 : 
                   clamp(mix(i * ts, tm + f, sdfBlendF), tm + ts * minStepF, intervalLen);

        vec3 p = x + dir * tm;

        f = Map(p);

        if (f < 0.0)
        {
            t1 = tm;
            f1 = f;

            if (minBinaryCount == 0.0)
            {
                hit = true;
            }
            else
            if(!hit)
            {
                hit = true;

                i = min(i, stepCount - minBinaryCount);
            }
        }
        else
        {
            t0 = tm;
            f0 = f;
        }
    }

    {
        float w = clamp01(f0 / (f0 - f1));
        tm = t0 * (1.0 - w) + t1 * w;
    }

    return hit;
}

bool ImplicitRC(
    vec3 x,
    vec3 dir,
    float intervalLen,

    float stepCount,
    float minBinaryCount,
    float sdfBlendF,
    float minStepF,

    out float t0, out float tm, out float t1, inout vec3 N)
{
    bool hit = false;

    float stepSize = 1.0 / stepCount;
    float ts = intervalLen * stepSize;

    t0 = tm = t1 = 0.0;

    float f  = 0.0;
    float f0 = 0.0;
    float f1 = 0.0;

    bool calcN = false;
    vec3 e = vec3(1.0);

    for (float i = 0.0; i <= stepCount; ++i)
    {
        tm = hit || calcN ? (t0 + t1) * 0.5 : 
                            clamp(mix(i * ts, tm + f, sdfBlendF), tm + ts * minStepF, intervalLen);

        vec3 p = x + dir * tm;

        p += e * (calcN ? 1.0/1024.0 : 0.0);

        f = Map(p);

		if(calcN)
        {
            N += e * f;
            e = vec3(e.z, -e.yx);            
        }
        else
        {
            if (f < 0.0)
            {
                t1 = tm;
                f1 = f;

                if (minBinaryCount == 0.0)
                {
                    hit = true;
                }
                else
                if(!hit)
                {
                    hit = true;

                    if(i == 0.0) return true;

                    i = min(i, stepCount - minBinaryCount);
                }
            }
            else
            {
                t0 = tm;
                f0 = f;
            }

            if(i == stepCount)
            {
                N = vec3(0.0);

                calcN = true;
                i = 0.0;
                stepCount = 4.0;
            }
        }
    }

    {
        float w = clamp01(f0 / (f0 - f1));
        tm = t0 * (1.0 - w) + t1 * w;
    }

    return hit;
}

// Lincense: CC0 (https://creativecommons.org/publicdomain/zero/1.0/)


#define KeyBoard iChannel1
float ReadKey(int keyCode) {return texelFetch(KeyBoard, ivec2(keyCode, 0), 0).x;}
float ReadKeyToggle(int keyCode) {return texelFetch(KeyBoard, ivec2(keyCode, 2), 0).x;}

#define VarTex iChannel0
#define OutCol col
#define OutChannel w

#define WRITEVAR(v, cx, cy) {if(uv.x == float(cx) && uv.y == float(cy)) OutCol.OutChannel = v;}
#define WRITEVAR2(v, cx, cy) {WRITEVAR(v.x, cx, cy) WRITEVAR(v.y, cx, cy + 1)}
#define WRITEVAR3(v, cx, cy) {WRITEVAR(v.x, cx, cy) WRITEVAR(v.y, cx, cy + 1) WRITEVAR(v.z, cx, cy + 2)}
#define WRITEVAR4(v, cx, cy) {WRITEVAR(v.x, cx, cy) WRITEVAR(v.y, cx, cy + 1) WRITEVAR(v.z, cx, cy + 2) WRITEVAR(v.w, cx, cy + 3)}

#define WriteVar(v, x) {WRITEVAR(v, x, 0) ++x;}
#define WriteVar2(v, x) {WRITEVAR2(v, x, 0) ++x;}
#define WriteVar3(v, x) {WRITEVAR3(v, x, 0) ++x;}
#define WriteVar4(v, x) {WRITEVAR4(v, x, 0) ++x;}

float ReadVar(int cx, int cy) {return texelFetch(VarTex, ivec2(cx, cy), 0).OutChannel;}
vec2 ReadVar2(int cx, int cy) {return vec2(ReadVar(cx, cy), ReadVar(cx, cy + 1));}
vec3 ReadVar3(int cx, int cy) {return vec3(ReadVar(cx, cy), ReadVar(cx, cy + 1), ReadVar(cx, cy + 2));}
vec4 ReadVar4(int cx, int cy) {return vec4(ReadVar(cx, cy), ReadVar(cx, cy + 1), ReadVar(cx, cy + 2), ReadVar(cx, cy + 3));}

float ReadVar(inout int x) { return ReadVar(x++, 0); }
vec2 ReadVar2(inout int x) { return ReadVar2(x++, 0); }
vec3 ReadVar3(inout int x) { return ReadVar3(x++, 0); }
vec4 ReadVar4(inout int x) { return ReadVar4(x++, 0); }

bool IsKnobStateTexel(vec2 uv)
{
    return uv.x < float(KnobMatCount) * 4.0 && uv.y >= float(KnobMatStateBuffY0) && uv.y < float(KnobMatStateBuffY0 + 4);
}

bool IsActiveTexel(vec2 uv)
{
    // state variables
    if(uv.y < 4.0) return true;

    // knob states
    if(IsKnobStateTexel(uv)) return true;

    // UI
    {
        // numerical value
        if(IsNumValueUITexel(uv)) return true;

        if(IsKnobUITexel(uv)) return true;
    }

    return false;
}

bool HandleStateAndDrawUI( out vec4 col, in vec2 uv0 )
{  
    Resolution = iResolution;

    col = vec4(0.0);
    vec2 uv = uv0 - 0.5;

    //if(IsActiveTexel(uv) == false) return false;

    int I = 0;
    vec4 iMouseLast     = ReadVar4(I);
    vec4 iMouseAccuLast = ReadVar4(I);
    vec4 wasdAccuLast   = ReadVar4(I);
    float frameAccuLast = ReadVar (I);
    ActiveKnobMatId = int(ReadVar(I));
    //float numKeyLast    = ReadVar (I);

    vec2 iMouseClick    = ReadVar2(I);

    //float numKey = 
    bool isClick = iMouseLast.z < 0.0 && iMouse.z >= 0.0;

    if(isClick) iMouseClick = iMouse.xy;

    bool shift = ReadKey(KEY_SHIFT) != 0.0;

    float kW = ReadKey(KEY_W);
    float kA = ReadKey(KEY_A);
    float kS = ReadKey(KEY_S);
    float kD = ReadKey(KEY_D);

    float left  = ReadKey(KEY_LEFT);
    float right = ReadKey(KEY_RIGHT);
    float up    = ReadKey(KEY_UP);
    float down  = ReadKey(KEY_DOWN);

    vec2 mouseDelta = iMouse.xy - iMouseLast.xy;

    bool didInteractUI = false;

    float knopMouseDelta = mouseDelta.y * (1.0 / 96.0 * (shift ? 0.125 : 1.0));

    {
        float i;
        if((i = KnobMatIdButtonMat(iMouseClick.xy)) >= 0.0)
        {
            if(isClick)
            ActiveKnobMatId = int(i);

            didInteractUI = true;
        }
    }

    // if current texel holds a knobs state update it if necessary
    if(IsKnobStateTexel(uv))
    {
        int ActiveKnobMatIdTemp = ActiveKnobMatId;

        ActiveKnobMatId = int(uint(uv.x) / 4u);

        bool IsKnobMatActuallyActive = ActiveKnobMatId == ActiveKnobMatIdTemp;

        ivec2 KnobMatStateBuffOrigin = ivec2(ActiveKnobMatId * 4, KnobMatStateBuffY0);

        KnobState knob;
    	if(GetKnob(ivec2(uv) - KnobMatStateBuffOrigin, iChannel0, /*doInit:*/ frameAccuLast == 0.0, /*out*/ knob))
        {
            if(!isClick && IsKnobMatActuallyActive && knob.colId >= 0.0)
            if(SqrLen(iMouseClick.xy - knob.p) < Pow2(knob.r.x))
            {
                float n = clamp(knob.n + knopMouseDelta * (knob.signed ? 2.0 : 1.0), knob.signed ? -1.0 : 0.0, 1.0);
                //n = round(n*32.0)/32.0;
                knob.n = n;
                didInteractUI = true;
            }

            col.w = knob.n;// write knob state
        }

        ActiveKnobMatId = ActiveKnobMatIdTemp;
    }

    // check for knob interaction (irrespective of whether current texel holds a knobs state or not)
    float knobVal = 0.0;
    {
        KnobState knob;
        if(GetKnob(iMouseClick.xy, iChannel0, /*doInit:*/ false, /*out*/ knob))
        {
            knobVal = knob.n;
            didInteractUI = true;
        }
    }

    // draw knob mat UI
    {
        KnobState knob;

        if(GetKnob(uv0, iChannel0, /*doInit:*/ frameAccuLast == 0.0, /*out*/ knob))
        {
            vec3 k = Knob(uv0, knob);
            col.rgb = k;
        }

    	ValueText(col, uv0, knobVal, iChannel2);
        KnobMatIdButtonMat(col, uv0);
    }



    bool anyK = false;

    anyK = anyK || iMouse.z > 0.0;
    anyK = anyK || shift;
    anyK = anyK || kW != 0.0;
    anyK = anyK || kA != 0.0;
    anyK = anyK || kS != 0.0;
    anyK = anyK || kD != 0.0;
    anyK = anyK || left  != 0.0;
    anyK = anyK || right != 0.0;
    anyK = anyK || up    != 0.0;
    anyK = anyK || down  != 0.0;


    float frameAccu = frameAccuLast + 1.0;
    //if(anyK) frameAccu = 0.0;


    vec4 wasdAccu = wasdAccuLast;
    wasdAccu += vec4(kW, kA, kS, kD);
    wasdAccu += vec4(up, left, down, right);        


    bool cond0 = iMouse.z > 0.0 && iMouseLast.z > 0.0;
    cond0 = cond0 && !didInteractUI;

    vec2 mouseDelta2 = cond0 && !shift ? mouseDelta.xy : vec2(0.0);
    vec2 mouseDelta3 = cond0 &&  shift ? mouseDelta.xy : vec2(0.0);

    vec4 iMouseAccu = iMouseAccuLast + vec4(mouseDelta2, mouseDelta3);



    int J = 0;
    WriteVar4(iMouse,       J);
    WriteVar4(iMouseAccu,   J);
    WriteVar4(wasdAccu,     J);
