{
    return ((h.x ^ (h.y >> 16u) ^ (h.z << 15u)) * rPhi4.x) ^ 
           ((h.y ^ (h.z >> 16u) ^ (h.w << 15u)) * rPhi4.y) ^
           ((h.z ^ (h.w >> 16u) ^ (h.x << 15u)) * rPhi4.z) ^
           ((h.w ^ (h.x >> 16u) ^ (h.y << 15u)) * rPhi4.w);
}

