/*
IN:
	rp		: ray start position
	rd		: ray direction (normalized)

	cp		: cube position
	cth		: cube thickness (cth = 0.5 -> unit cube)

OUT:
	t		: distances to intersection points (negative if in backwards direction)

EXAMPLE:	
	vec2 t;
	float hit = Intersect_Ray_Cube(pos, dir, vec3(0.0), vec3(0.5), OUT t);
*/
float Intersect_Ray_Cube(
vec3 rp, vec3 rd, 
vec3 cp, vec3 cth, 
out vec2 t)
{	
	rp -= cp;

	vec3 m = 1.0 / -rd;
	vec3 o = If(lessThan(rd, vec3(0.0)), -cth, cth);

	vec3 uf = (rp + o) * m;
	vec3 ub = (rp - o) * m;

	t.x = max(uf.x, max(uf.y, uf.z));
	t.y = min(ub.x, min(ub.y, ub.z));

	// if(ray start == inside cube) 
	if(t.x < 0.0 && t.y > 0.0) {t.xy = t.yx;  return 1.0;}

	return t.y < t.x ? 0.0 : (t.x > 0.0 ? 1.0 : -1.0);
}

/*
[...]

OUT:
	n0 : normal for t.x
	n1 : normal for t.y

EXAMPLE:	
	vec2 t; vec3 n0, n1;
	float hit = Intersect_Ray_Cube(pos, dir, vec3(0.0), vec3(0.5), OUT t, n0, n1);
*/
float Intersect_Ray_Cube(
vec3 rp, vec3 rd, 
vec3 cp, vec3 cth, 
out vec2 t, out vec3 n0, out vec3 n1)
{	
	rp -= cp;

	vec3 m = 1.0 / -rd;
    vec3 os = If(lessThan(rd, vec3(0.0)), vec3(1.0), vec3(-1.0));
    //vec3 os = sign(-rd);
	vec3 o = -cth * os;


	vec3 uf = (rp + o) * m;
	vec3 ub = (rp - o) * m;

	//t.x = max(uf.x, max(uf.y, uf.z));
	//t.y = min(ub.x, min(ub.y, ub.z));

    if(uf.x > uf.y) {t.x = uf.x; n0 = vec3(os.x, 0.0, 0.0);} else 
                    {t.x = uf.y; n0 = vec3(0.0, os.y, 0.0);}
    if(uf.z > t.x ) {t.x = uf.z; n0 = vec3(0.0, 0.0, os.z);}

    if(ub.x < ub.y) {t.y = ub.x; n1 = vec3(os.x, 0.0, 0.0);} else 
                    {t.y = ub.y; n1 = vec3(0.0, os.y, 0.0);}
    if(ub.z < t.y ) {t.y = ub.z; n1 = vec3(0.0, 0.0, os.z);}


	// if(ray start == inside cube) 
	if(t.x < 0.0 && t.y > 0.0) 
    {
        t.xy = t.yx;  

        vec3 n00 = n0;
        n0 = n1;
        n1 = n00;

        return 1.0;
    }

	return t.y < t.x ? 0.0 : (t.x > 0.0 ? 1.0 : -1.0);
}

/*
IN:
	rp		: ray start position
	rd		: ray direction (normalized)

	sp2		: sphere position
	sr2		: sphere radius squared

OUT:
	t		: distances to intersection points (negative if in backwards direction)

EXAMPLE:	
	vec2 t;
	float hit = Intersect_Ray_Sphere(pos, dir, vec3(0.0), 1.0, OUT t);
*/
float Intersect_Ray_Sphere(
vec3 rp, vec3 rd, 
vec3 sp, float sr2, 
out vec2 t)
{	
	rp -= sp;

	float a = dot(rd, rd);
	float b = 2.0 * dot(rp, rd);
	float c = dot(rp, rp) - sr2;

	float D = b*b - 4.0*a*c;

	if(D < 0.0) return 0.0;

	float sqrtD = sqrt(D);
	// t = (-b + (c < 0.0 ? sqrtD : -sqrtD)) / a * 0.5;
	t = (-b + vec2(-sqrtD, sqrtD)) / a * 0.5;

	// if(start == inside) ...
	if(c < 0.0) t.xy = t.yx;

	// t.x > 0.0 || start == inside ? infront : behind
	return t.x > 0.0 || c < 0.0 ? 1.0 : -1.0;
}


vec3 Hue_to_RGB(float hue)
{
    float H6 = hue * 6.0;

	float R =       abs(H6 - 3.0) - 1.0;
	float G = 2.0 - abs(H6 - 2.0);
	float B = 2.0 - abs(H6 - 4.0);

	return clamp(vec3(R, G, B), 0.0, 1.0);
}

vec3 HSV_to_RGB(vec3 hsv)
{
    return (Hue_to_RGB(hsv.x) * hsv.y + vec3(1.0 - hsv.y)) * hsv.z;
}

float RGB_to_Hue(vec3 rgb)
{
    float v = (rgb.r - rgb.g - rgb.b) *  (1.0/6.0) + (1.0/3.0);

    return (rgb.b < rgb.g ? -v : v) + 0.5;     
}

vec3 RGB_to_HSV(vec3 rgb)
{
    float v = max(rgb.r, max(rgb.g, rgb.b));

    if(v == 0.0) return vec3(0.0);

    rgb /= v;

    float ds = min(rgb.r, min(rgb.g, rgb.b));
    float s = 1.0 - ds;

    if(ds == 1.0) return vec3(0.0, 0.0, v);

    rgb = (rgb - ds) / s;

    float h = RGB_to_Hue(rgb);

    return vec3(h, s, v);
}

vec3 EvalWaxMtl(vec3 V, vec3 N)
{
    vec3 up = normalize(vec3(0.0, 1.0, -1.0));
    vec3 Ld = normalize(vec3(0.5, 1.0, -0.4));
    vec3 L  = normalize(vec3(0.75, 0.5, -1.0));

    float LdN = dot(L, N);
    float VdN = dot(V, N);
    vec3 H = normalize(V + L);
	vec3 Hr = vec3(-H.xy, H.z);

    vec3 R = N * (2.0 * dot(N, V)) - V;

    float f = Pow3(clamp01(dot(N, V)));

    vec3 c = vec3(1.0, 0.04, 0.03) * 0.2;
    c *= 1.0 + f * 0.1;

    float diff = cubic(clamp01(dot(N, Ld) * 0.5 + 0.5));

    float fd = mix(f, 1.0, diff);

    vec3 col = mix(vec3(1.0, 0.0, 0.0)*diff*0.15,c, clamp01(dot(N, Ld)*0.8+0.2))*fd;

    float s = 0.5;
    col += (1.0/(1.025- clamp01(dot(N, H )))) * (s*0.0015*vec3(0.75, 0.9, 1.0))*cubic(clamp01(dot(R, up)*2.))*1.5;
    col += (1.0/(1.03 - clamp01(dot(N, Hr)))) *    0.0002*vec3(0.25, 0.6, 1.0);
    col += vec3(0.0, 0.5, 1.0) * (0.02*s) * cubic(clamp01((dot(R, up)*0.75+0.25)*2.));

    col += vec3(1.0, 0.1, 0.3)*0.05 *(1.0-fd)*f;
    col += vec3(1.0)*0.1*(1.0-diff)*(1.0-clamp01(VdN))*(c*c+c);
    col *= mix(1.0, 0.75, Pow4(1.0-clamp01(VdN)));
    col *= 1.3;

    float fuzz = Pow4(clamp01(LdN) / (clamp01(LdN) + clamp01(VdN) + exp2(-16.0)));
    col += fuzz * diff * (0.1 * vec3(0.1, 0.8, 1.0));

    col *= mix(0.5, 1.0, sqrt(clamp01(dot(N, V)))) * 1.05;

    return col;
}

// The MIT License | Copyright Â© 2013 Nikita Miropolskiy | https://www.shadertoy.com/view/XsX3zB
float SimplexGradNoiseC3(vec3 p, uint seed) 
{
     /* skew constants for 3d simplex functions */
	 const float F3 = 0.3333333;
	 const float G3 = 0.1666667;

	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 1.0 at the center of the surflet to 0.0 at the margin */
	 w = max(1.0 - 2.0*w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(normalize(PhiHash11(s      , seed).xyz), x );
	 d.y = dot(normalize(PhiHash11(s +  i1, seed).xyz), x1);
	 d.z = dot(normalize(PhiHash11(s +  i2, seed).xyz), x2);
	 d.w = dot(normalize(PhiHash11(s + 1.0, seed).xyz), x3);

	 /* 3. return the sum of the four surflets */
	return dot(d, (w * w) * (w))*0.785;
	//return dot(d, (w * w) * (w))*4.73;
	//return dot(d, (w * w) * (w * w))*0.86;
	//return dot(d, (w * w) * (w * w))*6.725;
}

