    return ivec2(x, y);
}

float ReadKnobValue(sampler2D stateBuffer, ivec2 xy)
{
    return texelFetch(stateBuffer, GetKnobMatStateBuffOrigin() + xy, 0).w;
}

bool IsKnobUITexel(vec2 uv)
{
    float width = KnobSizeTotal * 4.0;

    if(uv.x < width+4.0 && uv.y > GetKnobMatPosY1()-4.0 && uv.y < GetKnobMatPosY0() + 48.0+2.0)
    {
        return true;
    }

    return false;
}

float GetKnobUIMask(vec2 uv)
{
    float width = KnobSizeTotal * 4.0 + 0.0;

    float minY = GetKnobMatPosY1() - 1.0;
    float maxY = GetKnobMatPosY0() + 48.0 - 2.0;

    float r = 8.0;

    vec2 p = uv - vec2(width, maxY + minY) * 0.5;
    vec2 b = vec2(width, maxY - minY) * 0.5 - r;
    vec2 d = max(abs(p) - b, 0.0);

    return 1.0 - clamp01((dot(d, d) - r*r)*0.05);
}

bool IsNumValueUITexel(vec2 uv)
{
    if(uv.x < 51.0 && uv.y < 17.0) return true;
}

KnobState CreateMatKnob(ivec2 xy, bool signed, float n, float colId)
{
    float x = float(xy.x);
    float y = float(xy.y);

    KnobState knob;
    knob.p.x = 0.5 * KnobSizeTotal + x * KnobSizeTotal;
    knob.p.y = 0.5 * KnobSizeTotal + y * KnobSizeTotal;

    knob.p.y = GetKnobMatPosY0() - knob.p.y;

    knob.r = vec2(KnobOuterRadius, KnobCircleThick);
    knob.signed = signed;
    knob.colId = colId;

	knob.n = n;

    return knob;
}

bool GetKnob0(ivec2 xy, out KnobState knob)
{
    KnobState knob0;
    knob0.n = 1.25;
    knob = knob0;

    if(xy.x < 0 || xy.x > 3 || 
       xy.y < 0 || xy.y > 3) return false;

    if(ActiveKnobMatId < 0 || ActiveKnobMatId >= KnobMatCount) return false;

    int i = xy.x + xy.y * 4;

    i += ActiveKnobMatId * KnobMatKnobCount;

    const bool fals = false;
    const bool IsSigned[] = bool[]
    (
        //========  0  ========//
        fals, fals, fals, fals,// A
        fals, fals, fals, fals,// B
        fals, fals, fals, fals,// C
        fals, fals, fals, fals,// D

        //========  1  ========//
        fals, fals, fals, fals,// A
        fals, fals, fals, fals,// B
        fals, fals, fals, fals,// C
        fals, fals, fals, fals,// D

        //========  3  ========//
        fals, fals, fals, fals,// A
        fals, fals, fals, fals,// B
        fals, fals, fals, fals,// C
        fals, fals, fals, fals,// D

        false
    );

    const float DefaultValue[] = float[]
    (
        //======  0  ======//
        0.25, 0.33, 0.0, 0.,// A 
        0.5, 0.58, 0.0, 0.5,// B 
        0.0, 0.0, 0.0, 0.0,// C 
        0.0, 0.0, 0.0, 0.0,// D 

        //======  1  ======//
        0.25, 0.36, 0.0, 0.0,// A 
        0.5, 0.74, 0.0, 0.5,// B 
        0.0, 0.0, 0.0, 0.0,// C 
        0.0, 0.0, 0.0, 0.0,// D 

        //======  3  ======//
        0.0, 0.0, 0.0, 0.0,// A 
        0.0, 0.0, 0.0, 0.0,// B 
        0.0, 0.0, 0.0, 0.0,// C 
        0.0, 0.0, 0.0, 0.0,// D 

        0.0
    );

    const float ColId[] = float[]
    (
        //======  0  ======//
        0.0, 1.0, -.1, 2.0,// A 
        0.0, 1.0, -.1, 2.0,// B 
        -.1, -.1, -.1, -.1,// C 
        -.1, -.1, -.1, -.1,// D

        //======  1  ======//
        0.0, 1.0, -.1, 2.0,// A 
        0.0, 1.0, -.1, 2.0,// B 
        -.1, -.1, -.1, -.1,// C 
        -.1, -.1, -.1, -.1,// D

        //======  3  ======//
        -.1, -.1, -.1, -.1,// A 
        -.1, -.1, -.1, -.1,// B 
        -.1, -.1, -.1, -.1,// C 
        -.1, -.1, -.1, -.1,// D

        0.0
    );    

    knob = CreateMatKnob(xy, IsSigned[i], DefaultValue[i], ColId[i]);

    return true;
}


// get knob by position id xy
bool GetKnob(ivec2 xy, sampler2D stateBuffer, bool doInit, out KnobState knob)
{
    // fill knob with hardcoded settings
	if(!GetKnob0(xy, /*out*/ knob)) return false;

    // fill in knob value stored in state buffer
    if(!doInit) knob.n = ReadKnobValue(stateBuffer, xy);

    return true;
}

// get knob by uv
bool GetKnob(vec2 uv, sampler2D stateBuffer, bool doInit, out KnobState knob)
{
    ivec2 xy = GetKnobXY(uv);

    // is position id valid
    if(xy.x == -1 || xy.y == -1) return false;

    // does knob actually exist
    if(!GetKnob0(xy, /*out*/ knob)) return false;

    // does uv lie on knob disk
    if(SqrLen(uv - knob.p) >= Pow2(knob.r.x + KnobPadding)) return false;

    // fill in knob value stored in state buffer
    if(!doInit) knob.n = ReadKnobValue(stateBuffer, xy);

    return true;
}


vec3 Knob(vec2 uv, KnobState state)
{
    //if(state.colId < 0.0) return vec3(0.0);
    if(state.colId < 0.0) { state.n = 0.0; state.r.x *= 0.5; state.r *= 1.5; }

    uv -= state.p;

    float v = 0.0;

    float l = length(uv);

    v = abs(l - (state.r.x - state.r.y)) - state.r.y;

    float sh = 1.0;
    sh = clamp01(v * 0.18);
    sh = 1.0-(1.0-sh)*(1.0-sh);
    sh = mix(0., 1.0, sh);
    v = clamp01(v);

    v = 1.0 - v;
    float o;
    {
        float a = state.n;

        bool tc = state.signed;
        if(tc) a = a * 0.5 + 0.5;
        a *= 2.0;
        bool ac = a > 1.0;
        float m = clamp01((ac ? uv.x : -uv.x)+0.5);
        if(ac) a = a - 1.0;

        m = min(m, clamp01((tc ? 1.0 : -1.0) * dot(AngToVec((1.0-a)*Pi), uv)+0.5));
        if(!tc && ac) m = 1.0-m;

        o = m;
    }

    float r = v;
    r = v * mix(0.25, 0.95, o);

    if(state.signed && uv.x < 0.0) { r = v * mix(0.06, 0.6, o);}

    if(state.colId < 0.0) { r *= 0.25; sh = 1.0; }

    return vec3(r, 1.0 - sh, state.colId);
}

// https://www.shadertoy.com/view/4tfBzn
float TextSDF(vec2 p, float glyph, sampler2D glyphBuffer)
{
    p = abs(p.x - .5) > .5 || abs(p.y - .5) > .5 ? vec2(0.) : p;
    return 2. * (texture(glyphBuffer, p / 16. + fract(vec2(glyph, 15. - floor(glyph / 16.)) / 16.)).w - 127. / 255.);
}

void ValueText(inout vec4 col, vec2 uv0, float n, sampler2D glyphBuffer)
{
    vec2 p = uv0 * 0.5;

    vec2 scale = vec2(4., 8.);
    vec2 t = floor(p / scale);   

    if(t.x < 0.0 || t.x > 5.0 || t.y != 0.0) return;
    if((n == 0.0 || abs(n) == 1.0) && t.x > 1.0) return;
    if(t.x == 0.0 && n >= 0.0) return;


    float c = 0.0;

    if(t.x == 0.0) c = 45.0;
    else if(t.x == 1.0) c = n == 0.0 ? 48.0 : (abs(n) == 1.0 ? 49.0 : 46.0);
    else
    c = abs(n) == 1.0 ? 48.0 : 48.0 + mod(floor(abs(n)*1000.0 * exp2((4.0-t.x) * -(log2(10.0)/log2(2.0)))), 10.0);

    p = (p - t * scale) / scale;
    p.x = (p.x - 0.5) * 0.5 + 0.5;

    if (c == 0.) return;

    float sdf = TextSDF(p, c, glyphBuffer);

    sdf = smoothstep(-0.05, 0.05, sdf);

    col.r = (1.0 - sdf);// * 0.999;
}

float KnobMatIdButtonMat(inout vec4 col, vec2 uv)
{
    vec2 uv2 = vec2(uv.x, uv.y - GetKnobMatPosY0());

    float width = KnobSizeTotal * 4.0;
    float height = 48.0;
    float ButtonCountX = 4.0;
    float ButtonCountY = 2.0;

    if(uv2.x < width && uv2.y >= 0.0 && uv2.y < height)
    {
        float wi = width / ButtonCountX;
        float hi = height / ButtonCountY;

        float ix = 0.0;
        float iy = 1.0;

        ix = floor(uv2.x / wi);
        uv2.x = (uv2.x / wi - ix) * wi;

        iy = floor(uv2.y / hi);
        uv2.y = (uv2.y / hi - iy) * hi;
        iy = float(ButtonCountY) - 1.0 - iy;

        uv2 -= vec2(wi, hi) * 0.5;

        float buttonSizeMod = 2.0;
        float l = length(uv2) + buttonSizeMod;

        float ll = min(wi, hi) * 0.5;

        float i = ix + iy * ButtonCountX;

        bool isActive = int(i) == ActiveKnobMatId;

        float buttonOpacity = isActive ? 0.9 : 0.2;
        float shadowOpacity = isActive ? 0.6 : 0.4;

        if(int(i) >= KnobMatCount) { buttonOpacity *= 0.09; shadowOpacity = 0.0; }

        col.rgb = vec3(smoothstep(ll-3.0, ll-4.5, l)       * buttonOpacity , 
                   pow(smoothstep(ll    , ll-4.0, l), 2.0) * shadowOpacity, .0);

        return i;
    }

    return -1.0;
}

float KnobMatIdButtonMat(vec2 uv)
{
    vec4 col;
    return KnobMatIdButtonMat(col, uv);
}
