// The old school S curve

float inverse_gain( float x, float k )
{
    float s = sign(x-0.5);
    float o = (1.0+s)/2.0;
    return o - 0.5*s*pow(2.0*(o-s*x),1.0/k);
}

float gain( float x, float k )
{
    float s = sign(x-0.5);
    float o = (1.0+s)/2.0;
    return o - 0.5*s*pow(2.0*(o-s*x),k);
}

float inverse_gain2( float x )
{
#if 0
    float s = sign(x-0.5);
    x = x*2.0;
    x = 1.0 + s*(1.0-x); x = sqrt(x);
    x = 1.0 + s*(1.0-x);
    x = x/2.0;
    return x;
#else
    return (x<0.5) ? sqrt(2.0*x)/2.0 : 1.0-sqrt(2.0-2.0*x)/2.0;
#endif
}

float gain2( float x )
{
#if 0
    float s = sign(x-0.5);
    x = x*2.0;
    x = 1.0 + s*(1.0-x); x = x*x;
    x = 1.0 + s*(1.0-x);
    x = x/2.0;
    return x;
#else
    return (x<0.5) ? 2.0*x*x : 2.0*x*(2.0-x)-1.0;
#endif
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    const float k = 1.9997;

    float px = 1.0/iResolution.y;
    vec2  p = ( vec2((iResolution.y-iResolution.x)/2.0,0.0)+fragCoord)/iResolution.y;

    vec3 col = vec3(0.0);

    if( p.x>0.0 && p.x<1.0 )
    {
        col = vec3( 0.2 ) + 0.01*mod(floor(p.x*20.0)+floor(p.y*20.0),2.0);

        // blue: smoothstep
        float y1 = gain2( p.x);
        col = mix( col, vec3(0.0,0.4,0.8), 1.0-smoothstep( 0.0, 2.0*px, abs(p.y-y1) ) );

        // yellow: inverse_smoothstep
        float y2 = inverse_gain2( p.x );
        col = mix( col, vec3(1.0,0.7,0.0), 1.0-smoothstep( 0.0, 2.0*px, abs(p.y-y2) ) );

        // gray: smoothstep of inverse_smoothstep, back to identity
        float y3 = gain2( y2 );
        col = mix( col, vec3(0.5,0.5,0.5), 1.0-smoothstep(0.0, 1.5*px, abs(p.y-y3) ) );
    }

    fragColor = vec4( col, 1.0 );
}
