/*
 Tunnel flythrough (mouseable)
 Surface profile (heightmap) based on hexagonal Voronoi subdivision;
 scrolling (nonrepeating) heightmap stored in continually updated texture.
 Run in fullscreen mode to get highest resolution texture.
*/

#define USE_TEX 1  // = 0 (in both shaders) for non-textured version

#define AA  0      // optional antialiasing

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
float Fbm3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
float Maxv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec4 Loadv4 (vec2 vId);
#if USE_TEX
vec4 LoadTx (vec2 uv);
#else
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
void HexVorInit ();
vec4 HexVor (vec2 p);
#endif

#define N_DRONE 1

vec3 dronePos[N_DRONE], ltDir;
vec2 aTilt[N_DRONE], trkFx, trkFy, trkAx, trkAy, trkFw, trkAw;
float dstFar, tCur, radTun, lmpSep;
#if USE_TEX
float txOrg, txAsp;
#endif
int idObj;
const int idWal = 1, idFlr = 2, idLmp = 3, idLmpH = 4, idDrBod = 11, idDrLamp = 12, idDrCam = 13;
const float pi = 3.14159;

float TSurfHt (vec2 tc)
{
#if USE_TEX
  return LoadTx (tc * txAsp).x;
#else
  vec4 vc;
  vc = HexVor (4. * pi * radTun * tc);
  return max (0.3 * SmoothMin (0.4, 0.3 * vc.x, 0.1) * (1. - 0.3 * dot (vc.yz, vc.yz)) *
     (1. + 2.5 * vc.w), 0.);
#endif
}

float RdTun (vec3 p)
{
  float a, rh;
  a = 0.5 * (atan (- p.x, p.y) / pi + 1.);
#if USE_TEX
  rh = TSurfHt (vec2 ((p.z - txOrg) / (2. * pi * radTun), a));
#else
  rh = TSurfHt (vec2 (p.z / (2. * pi * radTun), a));
#endif
  return radTun + dot (trkAw, sin (trkFw * p.z)) -
     rh * (1. - 0.5 * smoothstep (0.5, 1., p.y / radTun));
}

vec3 TrackPath (float z)
{
  return vec3 (dot (trkAx, sin (trkFx * z)), dot (trkAy, sin (trkFy * z)), z);
}

vec3 TrackVel (float z)
{
  return normalize (vec3 (dot (trkAx, trkFx * cos (trkFx * z)),
     dot (trkAy, trkFy * cos (trkFy * z)), 1.));
}

float TunDf (vec3 p)
{
  vec3 q;
  vec2 rt;
  float dMin, d;
  dMin = dstFar;
  p.xy -= TrackPath (p.z).xy;
  d = RdTun (p) - length (p.xy);
  if (d < dMin) { dMin = d;  idObj = idWal; }
  d = (0.82 - 0.02 * p.x * p.x) * radTun + p.y;
  if (d < dMin) { dMin = d;  idObj = idFlr; }
  return 0.5 * dMin;
}

float TunRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = TunDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 TunNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (TunDf (p + e.xxx), TunDf (p + e.xyy), TunDf (p + e.yxy), TunDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float DroneDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  q = p;
  q.y -= 0.05;
  d = PrRoundCylDf (q.xzy, 0.2, 0.03, 0.07);
  DMIN (idDrBod);
  q.y -= 0.07;
  d = PrRoundBoxDf (q, vec3 (0.06, 0.02, 0.12), 0.04);
  DMIN (idDrLamp);
  q = p;
  q.y -= -0.05;
  d = PrSphDf (q, 0.17);
  DMIN (idDrCam);
  q = p;
  q.xz = abs (q.xz) - 0.7;
  d = min (PrCylAnDf (q.xzy, 0.5, 0.05, 0.05), PrCylDf (q.xzy, 0.1, 0.03));
  q -= vec3 (-0.4, -0.15, -0.4);
  d = min (d, PrRoundCylDf (q.xzy, 0.05, 0.03, 0.2));
  q -= vec3 (-0.3, 0.2, -0.3);
  q.xz = Rot2D (q.xz, 0.25 * pi);
  d = min (d, min (PrRoundCylDf (q, 0.05, 0.02, 1.), PrRoundCylDf (q.zyx, 0.05, 0.02, 1.)));
  DMIN (idDrBod);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, dSzFac;
  dMin = dstFar;
  q = p;
  q.xy -= TrackPath (q.z).xy;
  q.z = mod (q.z + lmpSep, 2. * lmpSep) - lmpSep;
  q.y -= 0.7 * radTun;
  d = PrCylDf (q.xzy, 0.07 * radTun, 0.01 * radTun);
  DMIN (idLmp);
  q.y -= 0.1 * radTun;
  d = PrCylDf (q.xzy, 0.005 * radTun, 0.1 * radTun);
  q.y -= 0.1 * radTun;
  d = min (d, PrCylDf (q.xzy, 0.02 * radTun, 0.15 * radTun));
  q.y -= - 0.7 * radTun;
  DMIN (idLmpH);
  dSzFac = 2.;
  dMin *= dSzFac;
  for (int k = 0; k < N_DRONE; k ++) {
    q = dSzFac * (p - dronePos[k]);
    q.yz = Rot2D (q.yz, - aTilt[k].y);
    q.yx = Rot2D (q.yx, - aTilt[k].x);
    dMin = DroneDf (q, dMin);
  }
  dMin /= dSzFac;
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec3 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e = vec2 (0.002, 0.);
  float h;
  h = RippleHt (p);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vn;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 vc;
  vec3 vn, col;
  float dstObj, dstTun, dFac, bh, s, spec, att;
#if ! USE_TEX
  HexVorInit ();
#endif
  dstTun = TunRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (min (dstTun, dstObj) < dstFar) {
    if (dstTun < dstObj) {
      ro += rd * dstTun;
      vn = TunNf (ro);
      dFac = (1. - smoothstep (0.3, 0.5, dstTun / dstFar));
      ro.xy -= TrackPath (ro.z).xy;
      if (idObj == idWal) {
        vn = VaryNf (8. * ro, vn, 8. * dFac * dFac * (1. - 0.5 * smoothstep (0.7, 1., - vn.y)));
        s = Fbm3 (32. * ro);
        col = mix (vec3 (0.4, 0.4, 0.45), vec3 (0.5, 0.4, 0.3), smoothstep (0.4, 0.5, s));
        spec = 0.01;
      } else if (idObj == idFlr) {
        s = dFac * (1. - smoothstep (-0.2, -0.1, dot (rd, vn)));
        vn = RippleNorm (ro.zx, vn, 4. * s);
        vn = VaryNf (16. * ro, vn, s);
        col = vec3 (0.5, 0.3, 0.1) * (1. - 0.2 * smoothstep (0.4, 0.85,
           length (vec2 (ro.x, mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep)) / radTun));
        spec = 0.01;
      }
    } else {
      ro += rd * dstObj;
      vn = ObjNf (ro);
      spec = 0.1;
      if (idObj == idLmp) {
        col = vec3 (1., 1., 0.7) * 0.5 * (1. - vn.y);
        spec = -1.;
      } else if (idObj == idLmpH) {
        col = vec3 (0.6, 0.4, 0.1);
        spec = 0.;
      } else if (idObj == idDrBod) {
        col = vec3 (0.9, 0.7, 0.3) * (1. - 0.3 * smoothstep (0.4, 0.85,
           abs (mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep) / radTun));
        spec = 0.3;
      } else if (idObj == idDrLamp) {
        col = mix (vec3 (0., 1., 0.), vec3 (1., 0., 0.), step (0., sin (10. * tCur)));
        spec = -1.;
      } else if (idObj == idDrCam) {
        col = vec3 (0.1);
        spec = 0.3;
      }
    }
    if (spec >= 0.) col = col * (0.2 + 0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, ltDir), 0.) +
       spec * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.));
    s = min (dstTun, dstObj);
    att = SmoothMin (350. / pow (s, 2.), 1., 0.5) * (1. - smoothstep (60., 70., s));
    col *= att;
  } else col = vec3 (0.);
  return clamp (mix (col, vec3 (col.b), 0.2) * mix (1., smoothstep (0., 1.,
     Maxv3 (col)), 0.2), 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, vuPos, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  stDat = Loadv4 (vec2 (0., 0.));
  vuPos = stDat.y;
#if USE_TEX
  txOrg = stDat.z;
  txAsp = stDat.w;
#endif
  stDat = Loadv4 (vec2 (1., 0.));
  radTun = stDat.y;
  az = stDat.z;
  el = stDat.w;
  trkFx = 2. * pi * vec2 (0.005, 0.009);
  trkAx = radTun * vec2 (2.2, 1.7);
  trkFy = 2. * pi * vec2 (0.017, 0.023);
  trkAy = radTun * vec2 (0.13, 0.07);
  trkFw = 2. * pi * vec2 (0.05, 0.03);
  trkAw = radTun * vec2 (0.07, 0.04);
  vd = TrackVel (vuPos);
  az += 0.5 * pi + atan (- vd.z, vd.x);
  el += asin (vd.y) - 0.02 * pi;
  zmFac = 3.5 - 0.5 * SmoothBump (0.3, 0.7, 0.1, abs (az) / pi);
  for (int k = 0; k < N_DRONE; k ++) {
    dronePos[k] = TrackPath (vuPos + sign (0.5 * pi - abs (az)) * radTun * (1.5 + 3. * float (k)));
    dronePos[k].y += 0.1 * radTun * (1. + ((0.5 - float (k)) * sin (0.2 * tCur)));
    aTilt[k] = vec2 (TrackVel (dronePos[k].z).x, 0.25);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = TrackPath (vuPos) + vuMat * vec3 (0., 0.2, - 0.5) * radTun;
  dstFar = 100.;
  lmpSep = 10.;
  ltDir = vuMat * normalize (vec3 (0., 0.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 4.;
#endif  
  col = vec3 (0.);
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) *
       Rot2D (vec2 (0.71 / canvas.y, 0.), 0.5 * pi * (a + 0.5)), zmFac));
    rd.zx = Rot2D (rd.zx, 0.1 * atan (ro.x));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (clamp (col, 0., 1.), vec3 (0.7)), 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

#if ! USE_TEX

vec2 gVec[7], hVec[7];
const float sqrt3 = 1.73205;

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.8;
  p = p.yx;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

#endif

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

#if USE_TEX
vec4 LoadTx (vec2 uv)
{
  return texture (txBuf, uv);
}
#endif

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

