#define USE_TEX 1  // = 0 (in both shaders) for non-textured version

vec4 Loadv4 (vec2 vId);
void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord);
#if USE_TEX
float SmoothMin (float a, float b, float r);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
void HexVorInit ();
vec4 HexVor (vec2 p);
#endif

const float pi = 3.14159;

#if USE_TEX

float TSurfHt (vec2 tc)
{
  vec4 vc;
  vc = HexVor (tc);
  return max (0.3 * SmoothMin (0.4, 0.3 * vc.x, 0.1) * (1. - 0.3 * dot (vc.yz, vc.yz)) *
     (1. + 2.5 * vc.w), 0.);
}

#endif

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec4 stDat;
  vec2 canvas, pxIv;
  float tCur, az, el, radTun, vuPos, s, si;
#if USE_TEX
  float txOrg, txAsp;
#endif
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  pxIv = floor (fragCoord);
#if USE_TEX
  txAsp = 3./8.;
  if (pxIv.x >= txSize.x || pxIv.y >= txSize.x * txAsp) discard;
#else
  if (pxIv.x >= 2. || pxIv.y >= 1.) discard;
#endif
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    el = clamp (el + 0.5 * pi * mPtr.y, -0.1 * pi, 0.1 * pi);
    az = az + 2. * pi * mPtr.x;
  }
  radTun = 4.;
  vuPos = 3. * tCur;
#if USE_TEX
  HexVorInit ();
  txOrg = vuPos;
  s = 2. * pi * radTun / txAsp;
  if (abs (az) > 0.6 * pi) txOrg -= s - radTun;
  else if (abs (az) > 0.4 * pi) txOrg -= 0.5 * s;
  else txOrg -= radTun;
  si = txSize.x / (2. * s);
  txOrg = floor (txOrg * si) / si;
  stDat = vec4 (TSurfHt (2. * ((s * pxIv / txSize + vec2 (txOrg, 0.)))), 0., 0., 0.);
#endif
  if (pxIv.y == 0.) {
#if USE_TEX
    if (pxIv.x == 0.) stDat.yzw = vec3 (vuPos, txOrg, txAsp);
#else
    if (pxIv.x == 0.) stDat.yzw = vec3 (vuPos, 0., 0.);
#endif
    else if (pxIv.x == 1.) stDat.yzw = vec3 (radTun, az, el);
  }
  Savev4 (pxIv, stDat, fragColor, fragCoord);
}

#if USE_TEX

vec2 gVec[7], hVec[7];
const float sqrt3 = 1.73205;

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.8;
  p = p.yx;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

#endif

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  d = abs (fCoord - vId - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
