void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor=texture(iChannel0, uv); 
	//fragColor.z=(fragColor.z+fragColor.x)*0.99;
    vec2 m = iResolution.xy*.5-abs(fragCoord.xy-iResolution.xy*.5);
    m.x = min(m.x,m.y);
    if((length(iMouse.xy-fragCoord))<50.0*min(1.,iMouse.z)||m.x<10.)
    {
        fragColor.x=(fract(dot(sin(fragCoord*vec2(157.1,113.7)),
                               vec2(353.17+iTime,383.093-iTime)))>0.33)?0.0:1.0;
        fragColor.yz = fragColor.xx;
    }
    else
    {
        float t = iTime;
		vec4 u=vec4(0.0);
        float a = atan(uv.y-.5,uv.x-.5);
        a+=sin(length(uv-.5)*10.+iTime)*.5;
        fragCoord.x+=(cos(a)*3.);//floor(sin(iTime)*3.);
        fragCoord.y+=(sin(a)*3.);
    	u+=texture(iChannel0,(fragCoord+vec2(-1.0,-1.0))/iResolution.xy); 
    	u+=texture(iChannel0,(fragCoord+vec2( 0.0,-1.0))/iResolution.xy); 
	    u+=texture(iChannel0,(fragCoord+vec2( 1.0,-1.0))/iResolution.xy); 
  		u+=texture(iChannel0,(fragCoord+vec2(-1.0, 0.0))/iResolution.xy); 
  		u+=texture(iChannel0,(fragCoord+vec2( 1.0, 0.0))/iResolution.xy); 
		u+=texture(iChannel0,(fragCoord+vec2(-1.0, 1.0))/iResolution.xy); 
    	u+=texture(iChannel0,(fragCoord+vec2( 0.0, 1.0))/iResolution.xy); 
    	u+=texture(iChannel0,(fragCoord+vec2( 1.0, 1.0))/iResolution.xy);

        if(u.x<=1.)
            u.x -=.04;
        else if(u.x>3.)
            u.x = 1.;
        else
            u.x = 0.;


        if(u.y<3.75)
			u.y=0.0;
        else
            u.y=1.;

            u.z/=9.;
        if(u.z<.01)
			u.z=1.;
        else if(u.z>2.5)
            u.z=0.;
        else
            u.z -=.001;



        fragColor = u;
    }
}
