// Lissajous curve
// http://en.wikipedia.org/wiki/Lissajous_curve

#define NUM 200.0
#define TOW_PI 6.28318
#define STEPS 300.

vec2 lissajous(float t, float a, float b, float d)
{
	return vec2(sin(a*t+d), sin(b*t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy)*2.0-1.0;
	uv.x *= iResolution.x / iResolution.y;

	float a = 2. + mod(iTime, 10.);
	float b = 4.0;
	float d = 1.;	// phase

	float m = .006;
    vec2 lp = lissajous(log(iTime), a, b, d)*0.8;

    vec3 pixel = vec3(0.0, 0.0, 0.0);
   	vec3 color = vec3(0.0, 0.0, 0.0);

    float radius = 0.005;

    for	(float i = 0.0; i < NUM; i++) {
        float t = float(i)*TOW_PI / STEPS;
		t += log(iTime);
        vec2 p = lissajous(t, a, b, d)*0.8;

		// distance to line
        vec2 pa = uv - p;
        vec2 ba = lp - p;
        float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
        vec2 q = pa - ba*h;

        m = min( m, dot( q, q )*50.);


        pixel += vec3(1., 1., 1.) * m;
     }

    fragColor = mix(vec4(uv,0.8+0.5*sin(iTime),1.0), vec4(pixel, 1.0), 0.8);
}
