float trap = 0.;

float map(vec3 p)
{
	vec3 c = vec3(3., 4., 1.);
	p.z += iTime;
	p.xyz = mod(p.xyz, c)-c*.5;
	trap = distance(p, vec3(0.));
	return length(p*p.x*cos(p.z*p.y*.5/p.x))-1.2;
}

vec3 calcNormal(vec3 p)
{
	vec2 e = vec2(0.005, 0);
	return normalize(vec3(map(p+e.xyy)-map(p-e.xyy), map(p+e.yxy)-map(p-e.yxy), map(p+e.yyx)-map(p-e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = (2.*fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 ro = vec3(uv, 1.);
	vec3 rd = normalize(vec3(uv, -1.));

	float t = 0.;
	for (int i = 0; i < 100; i++)
	{
		float m = map(ro + rd * t);
		t+=m;
		if (t > 40. || m < 0.02) break;
	}

	if (t > 40.)
	{
		fragColor = vec4(0.);
		return;
	}

	vec3 p = ro + rd * t;
	vec3 n = calcNormal(p);

	vec3 lp = vec3(1., 3., 5.);
	vec3 ld = lp - p;
	float len = length(ld);
	ld /= len;
	float diff = max(dot(ld, n), 0.);
	float atten = min(1., 1./len);
	float amb = .25;
	float spec = pow(max(dot(normalize(ro-p), reflect(-ld, n)), 0.), 8.);

	vec3 col = vec3(trap, trap*trap, pow(trap, 4.)) * ((diff+amb)+spec)*atten;

	col += abs(tan(col + iTime))*.1;
	fragColor = vec4( col, 1.0 );

}
