void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	time = iTime * 3.0;
    vec2 _p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    vec3 ray = normalize(vec3(_p, 1.0));
    vec3 cam = vec3(0.0, 0.0, 0.0);
    bool hit = false;
    getCamPos(cam, ray);

    float depth = 0.0, iter = 0.0;
    vec3 d;
    vec3 p;

    for( int i = 0; i < 80; i ++)
    {
    	p = depth * ray + cam;
        d = map(p);

        if (d.x < 0.001) {
			hit = true;
            break;
        }
        if ( depth > 20.0)
            break;


		depth += d.x * 0.2;
		iter++;

    }
    vec3 col = vec3(0.0);

    if(hit)
    	col = vec3(1.0 - iter / 80.0);


    vec3 lightCol = vec3(
        sin(p.z* 30.0) + 0.1, 
        sin(p.z* 40.0+ 3.14) + 0.1, 
        0.5) * 0.1;

    if(hit)
    // emissive
    if( d.y == 0.0)
    {
        col = lightCol;
    }
    else // solid
    {
        vec3 normal = -gradient(p, 0.001);
        col *= dot(ray, normal);
        vec3 light = vec3(cos(iTime), sin(iTime), 0.0);

        col += vec3(0.5, 0.9, 0.9) * dot(light, normal) * 0.1;

        vec3 ref = reflect(ray,normal);

        ref = ref * rotationMatrix(vec3(1.0, 1.0, 1.1), iTime/10.0);

    //    col *= texture(iChannel0, ref).rbg;
        col = pow(col, vec3(
        	1.0, 
            0.3, 
            0.2)) * 0.6;
    }

   	float dd = max(d.z, 0.0001);

	col += lightCol * (0.01 / (dd));

	col = clamp(col, vec3(0.0), vec3(1.0));

    fragColor = vec4((col), abs(d.z - d.x) < 0.001 ? 0.0: depth);

}

