vec3 alpha(vec4 x)
{
    return vec3(x.a == 1.0 ? vec3(0.0) : vec3(x.rgb));

}
vec3 mask(vec4 x)
{
    return vec3(x.a == 0.0 ? x.rgb : vec3(0.0));

}

