// Based on shader by Vamoss
// https://www.shadertoy.com/view/XtjSzw

#define TWO_PI 6.28318530718

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    float radius = .3;

    //tile
    vec2 frequency = vec2(33.0, 18.0);
    vec2 index = floor(frequency * st)/frequency;
    float centerDist = 1.0-length(index-0.5 * (sin(iTime)+1.));
    vec2 nearest = 2.0 * fract(frequency * st) - 1.0;

    //movement
    float velocity = 5.0;
    nearest.x += cos(iTime * velocity + centerDist * TWO_PI)*(1.0-radius);
    nearest.y += sin(iTime * velocity + centerDist * TWO_PI)*(1.0-radius);

    //circle
    float dist = length(nearest);
    float circle = step(radius, dist);

    //colors
    vec3 bgColor = vec3(0.0, 0.0, 0.0);

    float colorAngle = centerDist;
    vec3 circleColor = vec3(colorAngle*sin(iTime/5.0)+0.2, colorAngle*.4*cos(iTime/5.0+2.0)+0.2, colorAngle*.5*cos(iTime+2.0)+0.4);

    vec3 color = mix(circleColor, bgColor, circle);

    fragColor = vec4(color, 1.0);
    //fragColor = vec4(vec3(centerDist), 1.0);
}
