    vec4 circle ( vec2 uv, vec2 pos, float rad, vec3 color) {
        float d = length(pos - uv) - rad;
        float t = clamp (d, 0.0, 1.0);
        return vec4 (color, 1.0 - t);
            }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // focus on the point or defocus picture
    // 	1: expand
    // -1: compress
	int direction = -1;

    vec2 uv = fragCoord.xy;
    vec2 xy = uv / iResolution.xy;
    vec2 center = iResolution.xy * 0.5;
    float radius = 0.25 * iResolution.y;

    // Background layer
    vec4 layer1 = vec4(0.0, 0.0, 0.0, 1.0);

    //circle
    if ( direction == -1 && iTime < 1.35) {
            radius *= (5.0 * cos(iTime + 0.5));
    }else  if (direction == 1 && iTime < 1.35){
            radius *= (5.0 * cos(iTime + 4.5));
    }
    else{
        return;
    }

    vec4 texInfo = texture(iChannel0, xy);
   vec3 texColor = vec3 (texInfo.r, texInfo.g, texInfo.b);
    vec4 layer2 = circle ( uv, center, radius, texColor);

    // blend the two
   fragColor = mix(layer1, layer2, layer2.a);

}
