void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
	float time = iTime; 
	float ratio = min(iResolution.x,iResolution.y) / max(iResolution.x,iResolution.y);
	float aspect = max(iResolution.x,iResolution.y) / min(iResolution.x,iResolution.y);

/////////////////////// Smooth Step Version (green) /////////////////////////////////////////	

	float color1 = 0.0;
	float s = 0.6;

	float sx = 1.0 - (0.5 + (s * ratio) * 0.5) ;
	float sy = 1.0 - (0.5 + s * 0.5) ;   

	float tx = -0.3 * (-1.0);
	float ty = (-1.0 + (mod(time*0.4,2.0))) * (-1.0);

    vec2 lb = smoothstep(vec2(sx,sy),vec2(sx,sy),uv + vec2(tx,ty) );

	vec2 rt = smoothstep(vec2(sx,sy), vec2(sx,sy),1.0 - (uv+vec2(tx,ty))  );

    color1 = lb.x * lb.y * rt.x * rt.y;




///////////////////////////floor method Red///////////////////////////////

    float color2 = 0.0;
    s = 0.6;

	tx = 0.3 * (-1.0);
	ty = (-1.0 + (mod(time*0.4,2.0))) * (-1.0);

	sx = 1.0 - (0.5 + (s * ratio) * 0.5)  ;
	sy = 1.0 - (0.5 + s * 0.5)  ;

	lb = floor (vec2( sx + (uv.x + tx),sy + (uv.y + ty) ));
	rt = floor (vec2( sx + (1.0 - (uv.x + tx)),sy + 1.0-(uv.y + ty) ));


	color2 = 1.0-(lb.x + lb.y + rt.x + rt.y);


	fragColor.ra = vec2(color2,color2);
    fragColor.ga = vec2(color1,color1);



}
