*/

#define SCREEN_D -650.0
#define AMOUNT_OF_PLASMA 15
#define LIGHT vec3(500,1000.0,4000)


float mix(int p) {
    uint b = uint(p);
	b += 0x9e3779b9u;
	b ^= b >> 15;
	b *= 0x85ebca6bu;
	b ^= b >> 13;
	b *= 0xc2b2ae3du;
	b ^= b >> 16;
	return fract(abs(float(b)/4294967295.0));
}

float hash(float n) {
    return mix(int(n));
}

float hash(int n) {
    return mix(n);
}


float sdist(vec2 p, vec2 l0, vec2 l1) {
    vec2 v1 = l1 - l0;
    vec2 v2 = l0 - p;
    vec2 v3 = vec2(v1.y,-v1.x);
    return dot(v2,normalize(v3));
}


float dist(vec2 p, vec2 l0, vec2 l1) {
    return abs(sdist(p, l0, l1));
}




vec2 rotate(vec2 p, float a) {

    return vec2 (
        cos(a)*p.x - sin(a)*p.y,
        sin(a)*p.x + cos(a)*p.y);
}

vec2 irotate(vec2 p, float a) {
    return rotate(p, -a);
}

vec3 closestPoint(vec3 A, vec3 B, vec3 C) {
    vec3 d = (C - B) / distance(C,B);
    vec3 v = A - B;
    float t = dot(v,d);
    vec3 P = B + t * d;
    return P;
}

vec3 iproject(vec3 p)
{


    p.xy -= iResolution.xy/2.0;

    p.z -= 2000.0;
    p.xy /= iResolution.y/675.0;

    p.xy *= (SCREEN_D+p.z);

    p.xy /= (SCREEN_D);

    p.z += 2000.0;
    //p.z += 400.0;

    p.y += sin(iTime*.5)*200.0;
    p.x += cos(iTime*.35)*300.0;
    p.z += cos(iTime*.4)*300.0;
    p.yz = irotate(p.yz, sin(iTime*.3)*.04);
    p.xz = irotate(p.xz, cos(iTime*.3)*.02);
    p.yx = irotate(p.yx, cos(iTime*.5)*.02);

    p.yz =  irotate(p.yz,- 4.25+2.7+6.28*sin((iMouse.y)/iResolution.y));
    p.xz =  irotate(p.xz, 0.0+6.28*sin((iMouse.x)/iResolution.x));
    return p;

}


float displacement(vec3 p) {
    vec3 pp = p*=.0001;
    float height = texture(iChannel1, vec2(atan(pp.x,pp.y)*.7,pp.z*2.0)).r*900.0;
    return (abs(p.z)<.30)?height:height*smoothstep(0.0,1.0,clamp((.4-abs(p.z))*10.0, 0.0, 1.0));
}

vec4 calcLight(vec3 p, vec3 ps, out vec3 normout) {
    float dist;

    float stp = 5000.0;
    for (int i = 0; i < 30; i++) {
        dist = (5000.0 - displacement(p)) - length(p);
        if(dist < 0.0) {
            if(stp < .1) break;
            p -= ps*stp;
            stp *= .5;
        }
        p += ps*stp;
    }

    vec3 p2 = p;
    vec3 p2s = normalize(LIGHT-p2);
    p2 += p2s;
    float dist2;

    for (int i = 0; i < 30; i++) {
        dist2 = (5000.0 - displacement(p2)) - length(p2);
        p2 += p2s*dist2*.9;
        if(dist2 > 200.0) break;
    }

    vec3 light = LIGHT;
    vec3 l = light-p;
    float ll = length(l);

    vec3 norm = normalize(-p);
    l = normalize(l);
    float bri = dot(l,norm) / (ll*ll*.0000003);
   // bri = (dist2 > 200.0) ? bri : bri * .5;
    float val = (5000.0 - displacement(p)) - length(p);

    vec3 norm2;
    norm2.x = (5000.0 - displacement(vec3(p.x+10.0,p.y,p.z))) - length(vec3(p.x-10.0,p.y,p.z));
    norm2.y = (5000.0 - displacement(vec3(p.x,p.y+10.0,p.z))) - length(vec3(p.x,p.y-10.0,p.z));
    norm2.z = (5000.0 - displacement(vec3(p.x,p.y,p.z-10.0))) - length(vec3(p.x,p.y,p.z+10.0));

    norm2 = normalize(norm2);
    float bri2 = dot(normalize(p-light),norm2);
    bri2 = clamp(bri2, 0.0, 1.0);
    vec4 tex = vec4(0);
    if(length(p) < 4800.0)
    tex += texture(iChannel0, vec2(atan(p.x*.002,p.y*.002),p.z*.0002))*2.3;
    if(length(p) >= 4800.0)
    tex += texture(iChannel2, vec2(atan(p.x*.002,p.y*.002),p.z*.0002))*1.5;
    tex = pow(tex,vec4(2.2));
    return  vec4(bri*bri2*tex);

}

float rmod(vec3 cp) {
    float asdf = sin(atan(cp.y,cp.z)*36.0);
    float asdf2 = sin(atan(cp.x,cp.z)*36.0);
    return (asdf+asdf2)*3.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float spec = 0.0;
    vec2 coord = fragCoord-.5;
    //coord = rotate(coord, sin(coord.x*.01)*.1);
    vec3 p = iproject(vec3(coord, -2000));
    vec3 p2 = iproject(vec3(coord, -3000));
    vec3 ps = normalize(p2-p);
    float old_d = length(p);
    vec3 pg = vec3(0,0,0); //glass reflection
    vec3 psg = vec3(0,0,0);
    bool glass_reflect = false;

    vec3 pg2 = vec3(0,0,0); //glass reflection
    vec3 psg2 = vec3(0,0,0);
    bool glass_reflect2 = false;

        //glass sphere
    vec3 cp = closestPoint(vec3(0,0,0), p, p2);

    if( length(cp) > 800.0 && old_d > 800.0) {} else
    {
        float A = length(cp);
        float C = 800.0;
        float B = sqrt(C*C-A*A);
        p = cp - ps*B;
        //p+=rmod(normalize(p));
        spec += dot(reflect(ps, normalize(p)), normalize(LIGHT));
        pg = p;
        glass_reflect = true;
        psg = reflect(ps,normalize(p));
        ps = refract(ps, normalize(p), 1.0/1.05);
        ps = refract(ps, normalize(p), 1.08);
    }


    bool glass_out = false;


    float val = 0.0;
    float r = sin(iTime)*700.0;


    //cast rays
    for (int i = 0; i <50; i++) {
        float d = length(p);

        //glass sphere
        if(!glass_out && d > 800.0 && old_d < 800.0) {
            glass_out = true;
            break;
        }


        float dist = 100.0;
        //check ray against plasma
        float more = clamp(100.0 - (800.0 - d), 0.0, 100.0)/100.0;
        more = more*more*more*50.0;
        for (int j = 0; j < AMOUNT_OF_PLASMA; j++)
        {

            if(d > 801.0) break;

            vec3 lps = vec3(0,1,0);
            float h = hash(j);
            float time = iTime+float(j)*100.0;
            lps.xy = rotate(lps.xy,(h-.5)*6.28*time*.087+cos((d+more)/150.0-h*time*3.5)*.15);
            lps.yz = rotate(lps.yz,(h-.5)*6.28*time*.14+cos((d+more)/150.0-h*time*2.0)*.15);

            lps = normalize(lps);
            vec3 a1 = d*lps;
            float d2 = distance(p,a1);
            dist = min(dist,d2);
            float v = clamp((35.0+more)-d2, 0.0, 35.0);
            v += clamp(5.0 - (d - 60.0),0.0,20.0)*.05;
            if(d < 100.0) v -= v*(100.0-d)/100.0;
            val += v;

        }

        old_d = d;

        if(d < 50.0) { //small sphere in the middle
            ps = vec3(0,0,0);
            break;
        }

        p+=ps*max(min(dist*.5,clamp(808.0-d,0.0,1000.0)),1.0);
    }

    //glass sphere
    if(glass_out) {
        vec3 cp = closestPoint(vec3(0,0,0), p, p+ps);
        float A = length(cp);
        float C = 800.0;
        float B = -sqrt(C*C-A*A);
        p = cp - ps*B; 
        //p+=rmod(normalize(p));
        pg2 = p;
        glass_reflect2 = true;
        psg2 = reflect(ps,normalize(-p));
        ps = refract(ps, normalize(-p), 1.0/1.08);
        ps = refract(ps, normalize(-p), 1.05); 
    }

    //val += clamp(50.0-distance(p,vec3(0,0,0)), 0.0, 50.0)*100.0;
    val /= 85.0;
    vec3 col = vec3((val-1.0)*.5,(val-2.0)*.15,val);
    col = clamp(col, 0.0, 1.0);



    vec3 norm;
    vec3 norm2;
    vec3 norm3;
    fragColor = calcLight(p,ps, norm)*vec4(1,.9,.8,1)*.5;
    if(glass_reflect) fragColor += calcLight(pg,psg,norm2)*.3*vec4(1,.9,.8,1)*.5; //outer reflection
    if(glass_reflect2) fragColor += calcLight(pg2,psg2,norm3)*.1*vec4(1,.9,.8,1)*.5; //inner reflection

    //p.x+=iTime*100.0;
    fragColor += vec4(col,1.0);
    spec=clamp(spec,0.0,1.0);
    spec = pow(spec,300.0);
    fragColor += spec*vec4(1,.8,.7,1);
    fragColor=pow(fragColor,vec4(1.0/2.2));

}
