#define M_PI 3.1415926535897932384626433832795
// base shape
float base(vec2 uv, float t) {
    vec2 ndc = (uv - 0.5) * 2.0;
    float h = 1.0 - dot(ndc,ndc);
    float gray = sin(t + h * 2.0*M_PI);
    return gray;
}

// fractaling the base shape
float map(vec2 uv, float t) {
    float themin = 1.0;
    for (int i = 1; i < 3; i++) {
        vec2 ndc = (uv - 0.5) * 2.0;
        vec2 uv2 = fract(float(i)*ndc);
        // variant
        //vec2 uv2 = cos(1.0/float(i)*M_PI*ndc); // pow2?
        float h = base(uv2, t);
        themin = min(themin, h);
    }
    return themin;
}
vec3 image(vec2 uv, float t) {
    float z = map(uv,t); // map but change base given what base is
    float w = map(uv,t+0.5);
    float u = map(uv, t+1.0);
    return vec3(z,w,u);

}
vec2 ndc(vec2 uv) {
    return (uv - 0.5) * 2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(image(uv, iTime * 1.0),1.0);
}

