vec2 synth_(vec2 a1,vec2 a,vec2 a2,vec2 a3,vec2 nb,vec2 a4){
    return log(a1+a*abs(a2-abs(.5-a3*vec2(fract1(nb.x*.998),fract1(nb.y)))*a4));
}
#define synth(a1,a,a2,a3,nb,a4) synth_(vec2(a1),vec2(a),vec2(a2),vec2(a3),vec2(nb),vec2(a4))

vec2 banjo(vec2 a, vec2 nb){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,4./6.)
  + synth(1./8.,a,1./4.,1.,nb*2.,6./4.));
}

vec2 harp(vec2 a, vec2 nb){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,2.)
  + synth(1./8.,a,1./4.,1.,nb*2.,1./2.));
}

vec2 synth_drum(vec2 a, vec2 nb,float m2){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,4./m2)
  + synth(1./8.,a,1./4.,1.,nb*2.,m2/4.));
}

vec2 oud(vec2 a, vec2 nb){
    return a/16./2.*abs(log(.5+a*abs(abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2.))
      +.5*log(1./8.+a*abs(1./8.-abs(.5-vec2(fract1(nb.x*.998),fract1(nb.y)))/2.)))/2.
    ;
}

