#define R iResolution
#define MAX 40.

struct Plane {
    vec3 pos;
    vec3 normal;
    float shift;
};


vec4 sp[2];
Plane p[2];

vec3 N, L, Rfl;

float iPlane(vec3 ro, vec3 rd, Plane p){
    return (p.shift - dot(ro - p.pos,p.normal))/dot(rd,p.normal);
}

vec3 nPlane(Plane p){
    return p.normal;
}

float iSphere(vec3 ro, vec3 rd, vec4 sp){
    float t = dot(rd, sp.xyz - ro);
    vec3 rs = ro + rd * t - sp.xyz;
    float k = sp.w*sp.w - dot(rs,rs);
    float h = MAX;
    if( k < 0.){
        h = -1.;
    } else {
        h = sqrt(k);
        h = min(t - h, t + h);
    }
    return h;
}

vec3 nSphere(vec3 rp, vec4 sp){
    return (rp - sp.xyz)/sp.w;
}

vec2 intersect(vec3 ro, vec3 rd){
    int mat = 0;
    float d = MAX;

    float s = iSphere(ro, rd, sp[0]);
    float f = iPlane(ro, rd, p[0]);
    if(f <= 0.)f = MAX;
    if(s <= 0.)s = MAX;

    float s2 = iSphere(ro, rd, sp[1]);
    float f2 = iPlane(ro, rd, p[1]);
    if(s2 <= 0.)s2 = MAX;
//    if(f2 <= 0.)f2 = MAX;

   d = min(d,s);
    d = min(d,f);
    d = min(d, s2);
//    d = min(d, f2);


    if(d == s)mat = 0;
    if(d == s2)mat = 1;
    if(d == f)mat = 2;
//    if(d == f2)mat = 3;
    if(d == MAX)mat = -1;
    return vec2(d, mat);
}

float diffuse(vec3 N, vec3 L){
    return clamp(dot(N,L), 0.,1.);
}

// phong
float phong(vec3 N, vec3 L, vec3 V, float k){
    float d = diffuse(N,L);
    vec3 lRef = reflect(L,N);

    float c = pow(max(dot(lRef, -V),0.),k) * d;

    return c;
}

// blinn phong
float bPhong(vec3 N, vec3 L, vec3 V, float k){
	float d = diffuse(N,L);

	float c = pow(max(dot(normalize(L+V),N),0.),k) * d;

	return c;
}

vec3 getColor(vec2 i, vec3 rp, vec3 rd){
    vec3 c = vec3(0);
    if(i.y == 0.){
        N = nSphere(rp, sp[0]);
        c = vec3(1);
    }
    if(i.y == 1.){
        N = nSphere(rp, sp[1]);
        c = vec3(0);
        c += .1*diffuse(N,L);
    }
    if(i.y == 2.){
        N = nPlane(p[0]);
        float grid = ceil(sin(rp.x*2.)*sin(rp.z*2.));
        c = vec3(0);
        c += grid*diffuse(N, L);
    }

    if(i.y == 3.)
        N = nPlane(p[1]);
    return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (2.*fragCoord.xy - R.xy)/R.y;
    vec2 m = (2.*iMouse.xy - R.xy)/R.y;
    vec3 c = vec3(0);
    vec3 ro = vec3(0,0,1);
    vec3 rd = normalize(vec3(uv,0) - ro);
    if(iMouse.z > 0.)
        ro.xy += m*2.;

    sp[0] = vec4(1.5,0,-2.5,1);
    sp[1] = vec4(-1.5,0,-1.5,1);
    p[1] = Plane(sp[0].xyz, normalize(vec3(0,0,1)), -2.);
    p[0] = Plane(vec3(0), normalize(vec3(0,1,0)), -1.);
    vec3 light = vec3(sp[0].xyz);


    vec2 i = intersect(ro, rd);
    vec3 rp = ro + rd * i.x;
    L = normalize(light - rp);

    float shadow = 1.;
    if(dot(N,L) > 0. && i.y == 2.){
        vec2 ss = intersect(rp + L*.001, L);
        if(ss.y == 1.)shadow = min(ss.x/3., 1.);
    }

    c = shadow * getColor(i, rp, rd);

    for(int j = 0;j < 10; j++)if(i.y != 0.){
        Rfl = reflect( rd, N);
        i = intersect(rp+Rfl*.001, Rfl);
        rp = rp + Rfl*(i.x+.001);
        L = normalize(light - rp);
        rd = Rfl;
        shadow = 1.;
        if(dot(N,L) > 0. && i.y == 2.){
            vec2 ss = intersect(rp + L*.001, L);
            if(ss.y == 1.)shadow = min(ss.x/3., 1.);
        }
        c = shadow * getColor(i, rp, Rfl)/(float(j+1)*4.) + c;
        if(i.y == -1.)break;
    }

    c = sqrt(c);
    //c *= shadow;
    //c += max(dot(N,vec3(0,1,0)),0.)*.01;
//    c = vec3(shadow);
//    c = vec3((length(light - rp) - sh.x));
//    c = vec3((sh.y+1.)/6.);
//    c = vec3(1)*dot(-rd,N);
//    c = vec3(1)*dot(N,L);
    fragColor = vec4(c,1.);
}
