void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec2 gv = fract( uv*6. ) -.5;

    float m = .0;
    float t = iTime*77.5;
    float dist = length(uv);

    for (float y=-1.; y<=1.; y++){
        for (float x=-1.; x<=1.; x++){
        vec2 offs = vec2(x, y);

        float d = length(gv-offs);

        float r = mix(.025, 1., sin(t+dist)*.5+.5);
        m += smoothstep(r, r/7., d);

    }
    }


   // col.rb = gv;
    col += mod(m, 1.);

    fragColor = vec4(col,1.0);
}
