#define PI 3.14159265359
#define MAXITER 1000
//#define WALL

// Spinning Nested Squares by Bingle

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    #ifdef WALL
    uv*=2.0;
    uv+=vec2(0.2,0.06)*iTime;
    uv = mod(uv+vec2(0.5),1.0)-vec2(0.5);
    #endif

    float increment = mod(0.25*iTime+0.125,PI*0.5); // Angle increment (should always be less than Ï€/2)

    int depth = 0;
    bool fill = true;

    float scale = 1.0;
    float angle = 0.0;

    while (true){
        if (depth>MAXITER){
            break; // break statement so my gpu doesn't break
        }

        vec2 local = vec2(dot(uv,vec2(cos(angle),sin(angle))),dot(uv,vec2(-sin(angle),cos(angle)))); // Apply rotation

        if (max(abs(local.x),abs(local.y))<scale*0.5){
            depth++;
            scale *= 0.707106781187/cos(increment-0.785398163397); // These constants are (from left to right) the square root of 2 divided by 2 and pi divided by 4
            fill = !fill;
        }else{
            break; // We're outside of the square
        }

        depth++;
        angle+=increment;
    }

    vec3 col = vec3(1.0);

    if (depth>MAXITER){
        col = vec3(0.5); // Just average things out
    }

    if (fill){ // Invert colors
        col = vec3(1.0)-col;
    }

    fragColor = vec4(col,1.0);
}
