void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_res = iResolution.xy;
    float n_scl_min = min(o_res.x, o_res.y);
    vec2 o_trn = (fragCoord.xy-o_res*.5)/n_scl_min;
    vec2 o_trn_mouse = iMouse.xy/iResolution.x;
    float n_its = 2.;
    float n_it_nor1 = 1./n_its;
    float n_tau = radians(360.);
    float n = 0.;
    vec3 o_col = vec3(0.);
    o_trn*=2.;
    for(float n_it_nor = 0.; n_it_nor < 1.; n_it_nor += n_it_nor1){

        vec2 o = o_trn - vec2(
            sin(n_tau*n_it_nor), 
            cos(n_tau*n_it_nor)
        );
        float n2 = length(o);
        float n3 = sin(length(o_trn)*3.+iTime)*.5+.5;

        float b = n3*.1+(sin(n_it_nor*n_tau+iTime)*0.5+.5)
            *(sin(n2*9.+iTime)*.5+.5)*.2;

        float c = 5.+(sin(n_it_nor*n_tau+iTime)*.5+.5);
        o_col += vec3(
            -(b*1.2)/((1.-(sin(
                length(o)*(c+sin(iTime)*.1)
                *(sin(length(o_trn)*3.03+iTime+0.03)*.5+.5)*3.
            )*.5+.5))-1.-b*1.2), 
            -b*1.5/((1.-(sin(
                length(o)*(c+sin(iTime+n_it_nor)*.2)
                *(sin(length(o_trn)*3.06+iTime+0.06)*.5+.5)*3.
            )*.5+.5))-1.-b*1.5), 
            -b/((1.-(sin(
                length(o)*(c+(sin(n2*1.)*.5+.5)*0.2)
                *(sin(length(o_trn)*3.09+iTime+0.09)*.5+.5)*3.
            )*.5+.5))-1.-b)

        );
    }
    fragColor = vec4(o_col, 1.);
}
