#define S(v)      smoothstep(  9./R.y, 0., v )
float happy_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}

void mainImage( out vec4 O, vec2 U )
{
  vec2 uv = U/iResolution.xy-0.5;
  vec2 uv2 = U/iResolution.xy-0.5;
  vec4 o = O;
  vec2 q =U;
    vec2 R = iResolution.xy;
        o = vec4( iResolution, fract(tan(q*7.)));
    o += o.a * 9./abs( mod(o.a * o.y * iTime, o.x) - q.y)  - o;
    float l2 = sqrt(length(uv));
    float a2 = l2 * 9.0 - iTime;


    float t = iTime * .01 + ((.25 + .05 * sin(iTime * .1))/(length(uv) + .07)) * 2.2;
	float si = sin(t);
	float co = cos(t);
	mat2 ma = mat2(co, si, -si, co);

    U = 5.* ( U+U - R ) /R.y;
    uv*=5.*-ma;
    U= cos(-uv.x + a2) * U + sin(a2) * vec2(-U.y, U.x);
    float a = atan(U.y,U.x) - iTime, l = length(U+o.xy), PI = 3.14;
    a = (ceil( mod( 3.*a +PI, 2.*PI ) - PI ))/3.;
    U = l * vec2(cos(a),sin(a));
    O-=O;

    O +=    S( abs(U.x-.7)-.1 ) * (.5+.5*U.y)
      + .5* S( abs(U.x-.5)-.1 ) * (.5-.5*U.y)*mod(U.x,23.);

    float anim = sin(iTime * 12.0) * 0.1 + 1.0;  // anim between 0.9 - 1.1 
    O+= vec4(happy_star(uv2, anim) * vec3(0.25,0.5,0.55)*0.01, 1.0);

}
