vec2 tree(vec2 uv)
{
    float turns = mod(floor(time/2.0), 6.0)+3.0;
    uv = rotatez(uv.xy, PI/(1.0*turns)).xy;
    float theta = atan(uv.y, uv.x);

    float rot = float(int((theta/PI*.5+.5)*turns))/turns;

    vec2 xy = rotatez(uv.xy, 
                      PI*2.0*(rot)+PI/turns).xy;

    xy = sign(xy)*log(abs(xy));

    return vec2((theta*turns), (xy.x*PI*2.0));
}

vec2 cmul(vec2 v1, vec2 v2) {
	return vec2(v1.x * v2.x - v1.y * v2.y, v1.y * v2.x + v1.x * v2.y);
}

vec2 cdiv(vec2 v1, vec2 v2) {
	return vec2(v1.x * v2.x + v1.y * v2.y, v1.y * v2.x - v1.x * v2.y) / dot(v2, v2);
}

vec2 mobius(vec2 uv, vec2 multa, vec2 offa, vec2 multb, vec2 offb)
