float message(vec2 uv) { // to alter in the icon
    uv-=vec2(1.,10.); if ((uv.x<0.)||(uv.x>=32.)||(uv.y<0.)||(uv.y>=3.)) return -1.; 
    int i=1, bit=int(pow(2.,floor(32.-uv.x)));
    if (int(uv.y)==2) i=  928473456/bit; // 00110111 01010111 01100001 01110000
    if (int(uv.y)==1) i=  626348112/bit; // 00100101 01010101 01010000 01010000
    if (int(uv.y)==0) i= 1735745872/bit; // 01100111 01110101 01100001 01010000
 	return float(i-2*(i/2));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //  if (iResolution.y<200.) to display only in the icon 
    if (iResolution.y<2000.) {float c=message(fragCoord.xy/8.);if(c>=0.){fragColor=vec4(c);return;}}


    vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

}

vec2 mainSound( in int samp, float time )
{
	float times[30];
	float notes[30];
	float time_delta=0.25;
	float tt=time_delta;
	times[ 0]=tt;tt+=time_delta;
	times[ 1]=tt;tt+=time_delta;
	times[ 2]=tt;tt+=time_delta;
	times[ 3]=tt;tt+=time_delta;
	times[ 4]=tt;tt+=time_delta;
	times[ 5]=tt;tt+=time_delta;
	times[ 6]=tt;tt+=time_delta;
	times[ 7]=tt;tt+=time_delta;
	times[ 8]=tt;tt+=time_delta*1.1;
	times[ 9]=tt;tt+=time_delta*1.2;
	times[10]=tt;tt+=time_delta*1.3;
	times[11]=tt;tt+=time_delta*1.5;
	times[12]=tt;tt+=time_delta*2.0;
	times[13]=tt;tt+=time_delta*2.5;
	notes[0]=1.0;       //c+
	notes[1]=-12.0+8.0; //<g+>
	notes[2]=1.0;       //c+
	notes[3]=3.0;       //d+
	notes[4]=5.0;       //f
	notes[5]=1.0;       //c+
	notes[6]=5.0;       //f
	notes[7]=8.0;       //g+
	notes[8]=+12.0+0.0; //>c<
	notes[9]=7.0;       //g
	notes[10]=5.0;      //f
	notes[11]=2.0;      //d
	notes[12]=4.0;      //e
	int time_index=-1;
	float time_top=0.0;
	float note=1.0;
	for(int i=0;i<13;i++){
		if(time_index<0 && time<times[i]){
			time_index=i;
			if(i!=0)time_top=times[i-1];
			float note_tmp=notes[i];
			note=pow(pow(2.0,note_tmp),1.0/12.0)*1.0;
		}
	}
	float time3=time-time_top;
    float vol=0.5;
	return vec2( sin(6.2831*440.0*note*time3)*exp(-3.0*time3)*vol );
}
