float pi = 3.141592653589793238462643383;

#ifdef GL_FRAGMENT_PRECISICION_HIGH
precision highp float;
#else
precision mediump float;
#endif

float map(vec3 p)
{
    float time = iTime;
    vec2 uv = gl_FragCoord.xy / iResolution.xy;
    // length(max(vec3(dot(cos(p), sin(p))), cos(p))*2.0-1.0) - 3.0;
    return length(sin(p)*dot(cos(p),tan(p))*2.0-1.0)-3.0;
}
vec3 trace(vec2 uv)
{
    float time = iTime;
    float t = 0.0;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 rd = normalize(vec3(uv, 1.0));
    rd.xz *= mat2(cos(time/2.0), -sin(time/2.0), sin(time/2.0), cos(time/2.0));
    vec3 ro = vec3(cos(time)+iTime/2., sin(6.0)-3., sin(time));
    for(int i = 0; i < 24; i++)
    {
        vec3 p = ro+t*rd;
        float d = map(p);
        t += d * 0.5;
    }
    float fog = 1.0/(1.0+t*t*0.1);
    vec3 fc = vec3(fog);
    return fc;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 p = normalize(vec3(uv, 1.0));
    vec3 x = trace(uv);
	fragColor = vec4( cos(uv.y) ) +
        sin(p.z)/2.
        *vec4(.5,.2,0.,1.)
        +vec4(0.25,0.25,0.,1.)
        -vec4(x, 1.0);
}
