/**
	The BRDF used in this shader is based on those used by Disney and Epic Games.

	The input parameters and individual components are modelled after the ones
	described in

		https://de45xmedrsdbp.cloudfront.net/Resources/files/2013SiggraphPresentationsNotes-26915738.pdf

	The various components are then combined based on Disney's PBR shader, found here

		https://github.com/wdas/brdf/blob/master/src/brdfs/disney.brdf

	I'd recommend reading this for a description of what the parameters in this BRDF do

		http://blog.selfshadow.com/publications/s2012-shading-course/burley/s2012_pbs_disney_brdf_notes_v3.pdf


*/

//GOLDEN GRAIL
#define GRAIL_ROUGHNESS 0.3
#define GRAIL_METAL 1.0
#define GRAIL_COLOUR vec3(0.854902, 0.690196, 0.368627)
#define BUMP_DEPTH 0.005

//RED PLASTIC GRAIL
//#define GRAIL_ROUGHNESS 0.1
//#define GRAIL_METAL 0.0
//#define GRAIL_COLOUR vec3(1.0, 0.0, 0.0)
//#define BUMP_DEPTH 0.0

float closeObj = 0.0;
const float PI = 3.14159;

mat3 rotX(float d){
    float s = sin(d);
    float c = cos(d);
    return mat3(1.0, 0.0, 0.0,
                0.0,   c,  -s,
                0.0,   s,   c );
}

mat3 rotY(float d){
    float s = sin(d);
    float c = cos(d);
    return mat3(  c, 0.0,  -s,
                0.0, 1.0, 0.0,
                  s, 0.0,   c );
}

mat3 rotZ(float d){
    float s = sin(d);
    float c = cos(d);
    return mat3(  c,  -s, 0.0,
                  s,   c, 0.0,
                0.0, 0.0, 1.0);
}

//iq's polynomial smooth minimum
float sMin(float a, float b){
    float k = 0.4;
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float gray(vec4 c){
    return 0.2126 * c.r + 0.7152 * c.g + 0.0722 * c.b;
}

//Distance functions from iq
float cylinder(vec3 p, vec2 h){
  vec2 d = abs(vec2(length(p.xz), p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec2 vecMin(vec2 a, vec2 b){
    if(a.x <= b.x){
        return a;
    }
    return b;
}

vec2 mapMat(vec3 p){
    //Cup
    vec3 cl = vec3(0.0, 0.0, 0.0);
    float bd = BUMP_DEPTH;
    float vsample = gray(texture(iChannel0, p.xy)) * bd;
    float hsample = gray(texture(iChannel0, p.xz)) * bd;
    float sph = length(p - vec3(cl.x, cl.y + 0.0, cl.z)) - 1.0 - vsample;
    float plane = -p.y + 0.5 - hsample; 
    float innerSphere = length(p - vec3(cl.x, cl.y + 0.0, cl.z)) - 0.955 + vsample;
    float stem = cylinder(p - vec3(cl.x, cl.y - 2.0, cl.z), vec2(0.25, 1.0)) - vsample;
    float base = cylinder(p - vec3(cl.x, cl.y - 2.0, cl.z), vec2(0.75, 0.025)) - hsample;
    vec2 cup = vec2(sMin(base, sMin(stem, max(-plane, max(-innerSphere, sph)))), 3.0);

    //Table
    vec2 table = vec2(cylinder(p - vec3(cl.x, cl.y - 2.325, cl.z), vec2(2.0, 0.1)) - gray(texture(iChannel1, p.xz)) * 0.02, 2.0);

    //Map
    vec2 cmap = vec2(-length(p) + 8.0, 1.0);
    return vecMin(cmap, vecMin(cup, table));
}

//Returns the min distance
float map(vec3 p){
    return mapMat(p).x;
}

float trace(vec3 ro, vec3 rd){
    float t = 1.0;
    float d = 0.0;
    float w = 1.3;
    float ld = 0.0;
    float ls = 0.0;
    float s = 0.0;
    float cerr = 10000.0;
    float ct = 0.0;
    float pixradius = 2.0 / iResolution.y;
    vec2 c;
    int inter = 0;
    for(int i = 0; i < 256; i++){
        ld = d;
        c = mapMat(ro + rd * t);
        d = c.x;

        //Detect intersections missed by over-relaxation
        if(w > 1.0 && abs(ld) + abs(d) < s){
            s -= w * s;
            w = 1.0;
            t += s;
            continue;
        }
        s = w * d;

        float err = d / t;

        if(abs(err) < abs(cerr)){
            ct = t;
            cerr = err;
        }

        //Intersect when d / t < one pixel
        if(abs(err) < pixradius){
            inter = 1;
            break;
        }

        t += s;
        if(t > 20.0){
            break;
        }
    }
    closeObj = c.y;
    if(inter == 0){
        ct = -1.0;
    }
    return ct;
}

//Approximate normal
vec3 normal(vec3 p){
    return normalize(vec3(map(vec3(p.x + 0.0001, p.yz)) - map(vec3(p.x - 0.0001, p.yz)),
                          map(vec3(p.x, p.y + 0.0001, p.z)) - map(vec3(p.x, p.y - 0.0001, p.z)),
                	      map(vec3(p.xy, p.z + 0.0001)) - map(vec3(p.xy, p.z - 0.0001))));
}

vec3 camPos = vec3(0.0);
vec3 lightPos = vec3(0.0);

//Determine if a point is in shadow - 1.0 = not in shadow
float shadow(vec3 ro, vec3 rd){
    float t = 0.01;
    float d = 0.0;
    float shadow = 1.0;
    for(int iter = 0; iter < 256; iter++){
        d = map(ro + rd * t);
        if(d < 0.0001){
            return 0.0;
        }
        if(t > length(ro - lightPos) - 0.5){
            break;
        }
        shadow = min(shadow, 128.0 * d / t);
        t += d;
    }
    return shadow;
}

float occlusion(vec3 ro, vec3 rd){
    float k = 1.0;
    float d = 0.0;
    float occ = 0.0;
    for(int i = 0; i < 25; i++){
        d = map(ro + 0.1 * k * rd);
        occ += 1.0 / pow(2.0, k) * (k * 0.1 - d);
        k += 1.0;
    }
    return 1.0 - clamp(3.0 * occ, 0.0, 1.0);
}

//Square
float sqr(float x){
  return x * x;
}

//Diffusion normalisation
float diff(float albedo){
  return albedo / PI;
}

//GGX NDF
float specD(float NdotH, float a){
  float asqr = sqr(a);
  float NdotHsqr = sqr(NdotH);
  return asqr / (PI * sqr((NdotHsqr) * (asqr - 1.0) + 1.0));
}

float G1(float NdotX, float k){
  return NdotX / (NdotX * (1.0 - k) + k);
}

//Geometric attenuation term
float specG(float NdotV, float NdotL, float k){
  k /= 2.0;
  return G1(NdotV, k) * G1(NdotL, k);
}

//Schlick fresnel approximation used by Unreal Engine
float fresnel(float AdotB){
  float power = pow(2.0, (-5.55473 * AdotB - 6.98316) * AdotB);
  return 0.04 + (1.0 - 0.04) * power;
}


vec3 BRDF(vec3 L, vec3 V, vec3 N, vec3 c, vec3 lc, float metallic, float roughness, float s, float o, float ccr){
  vec3 H = normalize(L + V);
  float NdotH = dot(N, H);
  float NdotL = dot(N, L);
  float NdotV = dot(N, V);

  if (NdotL < 0.0 || NdotV < 0.0) return vec3(0.0);

  float VdotH = dot(V, H);
  float alpha = roughness * roughness;

  float conductor = 1.0 - metallic;

  c = c - (0.4 - 0.4 * s) * c;

  vec3 specCol = mix(vec3(1.0), c, metallic);

  float FresL = fresnel(NdotL);
  float FresV = fresnel(NdotV);
  float Fresd90 = 0.5 + 2.0 * sqr(VdotH) * roughness;
  float Fresd = mix(1.0, Fresd90, FresL) * mix(1.0, Fresd90, FresV); 

  vec3 ref = vec3(0.0);  
  if(roughness > 0.5){
  	ref = pow(texture(iChannel2, reflect(V, N)).xyz, vec3(2.2)) * specCol * FresV;
  }else{
    ref = pow(texture(iChannel3, reflect(V, N)).xyz, vec3(2.2)) * specCol * FresV;
  } 

  float Ds = specD(NdotH, alpha);
  float FresH = fresnel(VdotH);
  vec3 Fress = mix(specCol, vec3(1.0), FresH);
  float Gs = specG(NdotV, NdotL, roughness);

  vec3 ccSpec = vec3(0.0);
  if(ccr > 0.0){
  	float ccDs = specD(NdotH, sqr(ccr));
  	vec3 ccFress = vec3(1.0);
  	float ccGs = specG(NdotV, NdotL, ccr);
    ccSpec = ccDs * ccFress * ccGs * floor(s);
  }

  return ((diff(conductor) * Fresd * max(0.0, NdotL) * o * c + Gs * Fress * Ds * floor(s) + ccSpec) + ref) * lc;
}

vec3 colour(vec3 p, float id){
    vec3 n = normal(p);
    vec3 l = normalize(lightPos - p);
    vec3 v = normalize(camPos - p);

    vec3 lc = pow(vec3(1.0, 0.72549, 0.631373), vec3(2.2));

    float o = occlusion(p, n);

    if(id == 1.0){
        return pow(texture(iChannel3, n).xyz, vec3(2.2));
    }
    if(id == 2.0){
        float s = shadow(p, l);
        vec3 samp;
        if(n.y > 0.9){
            samp = texture(iChannel1, p.xz).rgb;
        }else{
            samp = texture(iChannel1, p.xy).rgb;
        }
        samp = pow(samp, vec3(1.0 / 0.4545)); 
        return BRDF(l, v, n, samp, lc, 0.0, 0.3 + samp.r * 0.2, s, o, 0.0);
    }
    if(id == 3.0){
        float s = shadow(p, l);
        vec3 col = GRAIL_COLOUR;
        col = pow(col, vec3(1.0 / 0.4545));
        return BRDF(l, v, n, col, lc, GRAIL_METAL, GRAIL_ROUGHNESS, s, o, 0.0);
    }
    return vec3(0.0, 1.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    camPos = vec3(3.0 * sin(iTime / 2.0) , 1.0, 3.0 * -cos(iTime / 2.0));
    if(iMouse.w > 0.0){
        lightPos = vec3(((iMouse.x / iResolution.x * 2.0) - 1.0) * 5.0, (iMouse.y / iResolution.y) * 5.0, -3.0);
    }else{
        lightPos = vec3(2.0, 2.0, -3.0);
    }
    vec3 ro = camPos;
    vec3 rd = normalize(rotY(-iTime / 2.0) * rotX(radians(-30.0)) * vec3(uv, 1.0));
    float d = trace(ro, rd);
    vec3 c = ro + rd * d;
    vec3 col = vec3(0.0);
    //If intersected
    if(d > 0.0){
        //Colour the point
        col = colour(c, closeObj);
    }else{
        col = vec3(0.0);
    }
    col = pow( col, vec3(0.4545) );
	fragColor = vec4(col,1.0);
}
