vec3 getTertiaryColor(int index) {
    if (index == 0) return vec3(0.0, 0.5, 1.0); // Azure
    if (index == 1) return vec3(0.5, 0.0, 1.0); // Violet
    if (index == 2) return vec3(1.0, 0.0, 0.5); // Rose
    if (index == 3) return vec3(1.0, 0.5, 0.0); // Orange
    if (index == 4) return vec3(0.5, 1.0, 0.0); // Chartreuse
    if (index == 5) return vec3(0.0, 1.0, 0.5); // Spring Green
    return vec3(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(6.0, 6.0) * fragCoord/iResolution.xy;

    int index1 = int(uv.x);
    int index2 = int(uv.y);

    vec3 color1 = getTertiaryColor(index1);
    vec3 color2 = getTertiaryColor(index2);

    vec3 color = vec3(0.0);
    if (index1 == index2)
        color = color2;
    else if (index2 >= index1)
        color = 0.5 * (color1 + color2);
    else
        color = 0.33 * (color1 + color2);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
