void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = cos(iTime*.3)*.25+.29;

	vec2 uv = (fragCoord.xy / iResolution.xy)*zoom - (zoom-.2);
    vec2 uv1,uv2,uv3,uv4;
    uv1 = uv2 = uv3 = uv4 = uv;

    int iterations = int((iMouse.y/iResolution.y)*11. + 1.);
    if (iMouse.w < 1.) iterations = 11;

    vec3 col = vec3(0.);
    float d = 0.;
    for (int i = 0; i < 12; i++) {
        if (i > iterations) break;

        vec2 dir = vec2(.5/float(i+1), 0.);

        uv1 += dir;
        uv2 += dir.yx;
        uv3 -= dir;
        uv4 -= dir.yx;

        float size = (.5/float(i+1));
        float sizeDiv = size/1.25;
    	d = max(d, length(max(abs(mod(uv1,size*2.)-size)-sizeDiv, 0.)));
        d = max(d, length(max(abs(mod(uv2,size*2.)-size)-sizeDiv, 0.)));
        d = max(d, length(max(abs(mod(uv3,size*2.)-size)-sizeDiv, 0.)));
        d = max(d, length(max(abs(mod(uv4,size*2.)-size)-sizeDiv, 0.)));

    	col = (vec3(sin(d*160.2345+float(i)*.1+uv3.y*36.),
                    cos(d*120.2+float(i)*.1+uv2.x*36.),
                    -cos(d*240.4+uv1.y*36.))*.5+.5)*
               max(0., 1.-d*20.);
    }

    fragColor = vec4(col,1.);//max(0., 1.-d*20.));
}
