float plot(vec2 st, float pct){
  return  smoothstep( pct-0.01, pct, st.y) -
          smoothstep( pct, pct+0.01, st.y);
}

float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }

float circle(vec2 fragCoord, vec2 centreCoord, float radius)
{
    return radius / length(fragCoord - centreCoord);
}

vec3 drawStar(vec2 fragCoord, vec2 centreCoord, float radius)
{
    float r = circle(fragCoord, centreCoord, tan(iTime + 1.0) * radius + 100.);
    float g = circle(fragCoord, centreCoord, tan(iTime + 1.2) * radius + 100.);
    float b = circle(fragCoord, centreCoord, tan(rand(iTime) + 0.7) * radius + 100.);

    return vec3(r,g,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 centre = iResolution.xy / 2.0;

    vec3 val = drawStar(fragCoord, centre, 50.0);

    fragColor = vec4(val,1.0);
}
