#define DynamicBuffer iChannel0 

void mainImage( out vec4 C, in vec2 U )
{
	U -= floor(iResolution.xy*vec2(0.33,0.35));

    vec2 FFT = texelFetch(DynamicBuffer,ivec2(int(floor(U.x)),9),0).xy; 
    float energy = length(FFT);
    energy = U.x == 0.5 ? energy/512. : energy/256.;

    //Show Dynamic Buffer Technology
    C = texelFetch(iChannel0,ivec2(int(U.x)+130,int(U.y)+55),0);
    if(floor(U.x)>255. || floor(U.x) < 0.)
        return;
    if(floor(U.y) < energy && floor(U.y) > -1.)
        C.g = 1.;
    if(floor(U.y) < 128. && floor(U.y)>-1.)
        C.r = 0.2;
}

/*
	GPU FFT(Webgl2.0).glsl by ç¥å…ƒæ´ª(834144373)
	base on my "Pixel Shader FFT" https://www.shadertoy.com/view/lsVXWt by 834144373
*/
#define AudioBuffer iChannel1
#define DynamicBuffer iChannel0
#define BitWidth 9
#define _Half_PI 1.57079633
#define  _PI 3.1415926
#define _2PI 6.2831853
const ivec2 AudioBufferSize = ivec2(512,2);

//to visual debug
//if 200Hz/s
//#define debug_visual
#define debug_frequency 200. //range(1,255) Hz

//Any bit-length Bit Reverse
int BitReverse(int n,int _bitWidth){
	int r = 0;
    for (int i =_bitWidth-1;i >= 0;i--)
    {
		r |= (n&1)<<i;
		n >>= 1;
	}
    return r;
}

//Get (0.,1.) time domain values --> (-128,127) beacase the audio is 8 bits channel
float GetTimeDomain(int index){
	float d = texelFetch(AudioBuffer,ivec2(index,1),0).r;
    d = floor(d*255.+0.5)-128.; //round to int
    #ifdef debug_visual
        d += 128.*sin(debug_frequency*float(index)/float(AudioBufferSize.x)*_2PI);
    #endif
    return d;
}
vec2 GetFFT(int x,int y){
	return texelFetch(DynamicBuffer,ivec2(x,y),0).st;
}
/*
  U.y -1 is get time domain by bit reverse,
  U.y 0,1,2,3,4,5,6,7,8 is do butterfly step.
*/
vec2 GPUFFT(ivec2 U,inout vec2 B){
    --U.y;
    int index = BitReverse(U.x,BitWidth);
    if(U.y == -1)
    	B.st = vec2(GetTimeDomain(index),0.);
    if(U.y >= 0 && U.y <= 8)
    {
        int burb = 1<<U.y; 
        int kn = U.x & (burb-1);
        float C = -_PI*float(kn)/float(burb);
        vec2 W = sin(vec2(C+_Half_PI,C));
        int E = 1-((U.x>>U.y)&1);
        B.xy = GetFFT(U.x-burb*(1-E),U.y);
        B.xy += mat2(W,-W.y,W.x)*GetFFT(U.x+burb*E,U.y)*float(E*2-1);
    }
	return GetFFT(U.x,9).xy;
}

void mainImage( out vec4 C, in vec2 U )
{
	if(iFrame<2)
        C = vec4(0.);
    ivec2 UU = ivec2(U-0.5);
    if(UU.x > AudioBufferSize.x || UU.y>9){
    	C = vec4(0.);
        return;
    }
    GPUFFT(UU,C.xy);
}

