// to begin to learn how to code try:
// const float divs = 2.0;
float divs = 3.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )


{


    // run time backwards:

    // this is really cool:
    //float t = 800.0 - iTime;



    // 8.0 is a good speed
    float speed = 4.0;

    // best time is 400 for sure - "how far out to start"
    float t = 180.0 - (iTime * speed);


    // create staggered / staged division:
    divs += t / 1.0;


	vec2 div = vec2( divs, divs*iResolution.y/iResolution.x );
	vec2 uv = fragCoord.xy / iResolution.xy;


    // center on screen

    uv -= 0.5;
    //+ (iTime*0.01);									
    float b = 4.0*divs/iResolution.x;			// blur over 2.4 pixels
    vec2 xy = div*uv;

    vec2 S;
	S.x = (xy.x + xy.y)*(xy.x - xy.y)*0.5;		// "velocity potential"
    S.y = xy.x*xy.y;							// stream function

    // speed of the dots
    S.x -= iTime*4.0;						// animate stream

    vec2 sxy = sin(3.14159*S);
    float a = sxy.x * sxy.y;					// combine sine waves using product

    a = 0.5*a + 0.5;							// remap to [0..1]
	a = smoothstep( 0.85-b, 0.85+b, a );		// threshold

    float c = sqrt( a );						// correct for gamma

    //fragColor = vec4(sxy.x, c, sxy.y, 1.0);


    // blue lines yellow and white balls
    // fragColor = vec4(c, c, sxy.y, 1.0);


    // blue lines yellow and white balls
    fragColor = vec4(c*2.0, c, sxy.y, 1.0);
}
