//  Experiment showing dithering discussed by Mikkel Gjoel in https://youtu.be/RdN06E6Xn9E?t=20m24s

// Segments from top to bottom are:
// No quantisation
// Quantisation after triangular noise PDF applied
// Quantisation after uniform noise applied  -  note the moving bands of noise
// Quantisation without noise


#define HASHSCALE3 vec3(.1031, .1030, .0973)

float bands = 10.;

vec2 hash22(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float i = mod( (uv.x + iTime*0.1) ,1.);

    if (uv.y<0.75)    
    {
        vec2 rand = hash22(fragCoord);

        if (uv.y>0.5)
            i += (rand.x + rand.y - 0.5) / bands;  // Triangular PDF noise
        else if (uv.y>0.25)
            i += rand.x/bands;  // Uniform PDF noise

        i = floor(i*bands)/bands; // Quantisation
    }

    fragColor = vec4(i,i,i,1);
}
