// Interactive version of the "Curvature Blindness Illusion" by Kohske Takahashi ( https://www.ncbi.nlm.nih.gov/pubmed/29204264 )

// based on the code by FabriceNeyret2
// reproducing http://blogs.discovermagazine.com/neuroskeptic/2017/12/08/curvature-blindness-illusion/

#define S(v) smoothstep( 1.,-1., ( abs(v) - .1 ) * 40.0)  // antialiased thick curve

void mainImage( out vec4 O, vec2 U )
{
    O-=O;
	vec2 R =  iResolution.xy;
    vec2 U1 = 5.3* ( U+U - R ) / R.y;

    O += .45* step(-8.,U1.y-U1.x) + .55 * step(8.,U1.y-U1.x);       // background bands
    if (abs(U1.y)>5.) return;

    vec2 U2 = 0.028*vec2(U.x, U.y);
    vec2 U3 = U2 + vec2(-0.01*iMouse.x, 0.0);

    float lineNo = floor(mod(U2.y,2.));

    float v = .3 + .4*mod(floor((U3.x-.5)*lineNo+U2.x*(1.0-lineNo)),2.), // light/dark value along waves
         dy = .25*sin(3.14*U2.x) -  ( 2.*fract(U2.y)-1. );        // distance to sines
    O +=  S( dy )      * ( v - O );                             // pair of sines
    O +=  S( dy -.5 )  * ( v - O );

}
