//------------------House Properties--------------------//

//---Toggle---//
bool layerOneEnabled = true;

//---Position---//
vec2 housePos = vec2(0.5f,0.5f);

//---Dimensions---//
vec2 houseDimensions = vec2(0.3,0.2);

//---Color---//
vec3 colorOne = vec3(1.0f,0.0,0.0);




//------------------Chimney Properties--------------------//

//---Toggle---//
bool layerTwoEnabled = true;

//---Position---//
vec2 chimneyPos = vec2(0.3f,0.8f);

//---Dimensions---//
vec2 chimneyDimensions = vec2(0.1,0.1);

//---Color---//
vec3 colorTwo = vec3(1.0f,1.0f,1.0f);




//------------------Windows Properties--------------------//

//---Toggle---//
bool layerThreeEnabled = true;

//---Position---//
vec2 windowPos = vec2(0.3f,0.5f);
vec2 windowTwoPos = vec2(0.7f,0.5f);

//---Dimensions---//
vec2 windowDimensions = vec2(0.05f,0.05f);

//---Color---//
vec3 colorThree = vec3(0.0,1.0f,1.0f);




//------------------Background Properties--------------------//

//---Color---//
vec3 backgroundColor = vec3(0.0,1.0f,0.0);




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // The following code initializes the layers to their
    // appropriate values
    vec4 layerZero = vec4(backgroundColor,1.0);
    vec4 layerOne = vec4(colorOne,1.0);
    vec4 layerTwo = vec4(colorTwo,1.0);
    vec4 layerThree = vec4(colorThree,1.0);



    // The following code creates and applies the aspect
    // ratio
    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;

    housePos.y *= aspectRatio;
    chimneyPos.y *= aspectRatio;
    windowPos.y *= aspectRatio;
    windowTwoPos.y *= aspectRatio;
    uv.y *= aspectRatio;



    // The following code defines and creates the properties for
    // the layers
    float houseInterpX = step(housePos.x - houseDimensions.x, uv.x) - step(housePos.x + houseDimensions.x, uv.x);
    float houseInterpY = step(housePos.y - houseDimensions.y, uv.y) - step(housePos.y + houseDimensions.y, uv.y);
    layerOne.a = houseInterpX * houseInterpY * float(layerOneEnabled);


    float chimneyInterpX =  step(chimneyPos.x - chimneyDimensions.x, uv.x) - step(chimneyPos.x + chimneyDimensions.x, uv.x);
    float chimneyInterpY =  step(chimneyPos.y - chimneyDimensions.y, uv.y) - step(chimneyPos.y + chimneyDimensions.y, uv.y);
    layerTwo.a = chimneyInterpX * chimneyInterpY * float(layerTwoEnabled);


    float windowInterpX =  step(windowPos.x - windowDimensions.x, uv.x) - step(windowPos.x + windowDimensions.x, uv.x);
    float windowInterpY =  step(windowPos.y - windowDimensions.y, uv.y) - step(windowPos.y + windowDimensions.y, uv.y);

    windowInterpX +=  step(windowTwoPos.x - windowDimensions.x, uv.x) - step(windowTwoPos.x + windowDimensions.x, uv.x);
    windowInterpY +=  step(windowTwoPos.y - windowDimensions.y, uv.y) - step(windowTwoPos.y + windowDimensions.y, uv.y);

    layerThree.a = windowInterpX * windowInterpY * float(layerThreeEnabled);



    // The following code mixes the layers together
    vec4 sum = mix(layerZero, layerTwo, layerTwo.a);
    sum = mix(sum,layerOne, layerOne.a);
    sum = mix(sum, layerThree, layerThree.a);
    fragColor = sum;

}
