// modified based on FabriceNeyret2's comment. Thank you so much!!!
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
vec2 points[8];
vec2 mrg = vec2(19,8), mrg2 = vec2(8,8);
float gtime;

vec2 setYZ(float x, float y, float z, float rad) {        
    vec3 p = vec3(x,y,z);
	p.xz *= rot(rad);
    p.xy *= rot(rad);
    return floor(p.xy);
}

vec2 setXY(float x, float y, float z, float rad) {        
    vec3 p = vec3(x,y,z);
	p.yz *= rot(rad);
    p.xz *= rot(rad);
    return floor(p.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 p = (fragCoord.xy / iResolution.xy) * (iResolution.xy / min(iResolution.x, iResolution.y));
	vec3 color = vec3(0.0);

	float num = 100.0;

    color = vec3( sin(p.y * num) + cos(p.x * num + 3.5)- cos(p.y/2.) );

	gtime = iTime*2.5;

    points[0] = setYZ(0.,0.,4., gtime) + mrg;
    points[1] = setYZ(4.,0.,0., gtime) + mrg;
    points[2] = setYZ(0.,4.,0., gtime) + mrg;
	points[3] = setYZ(-2.,-2.,-2., gtime) + mrg;
    points[4] = setXY(0.,0.,4., -gtime) + mrg2;
	points[5] = setXY(4.,0.,0., -gtime) + mrg2;
	points[6] = setXY(0.,4.,0., -gtime) + mrg2;
	points[7] = setXY(-2.,-2.,-2., -gtime) + mrg2;

    vec2 temp = vec2(.063, 0.061);
    #define col(i, r,g,b) { vec2 q = abs( p/temp-points[i].xy - .5); if (max(q.x,q.y)<.5) color *= vec3(r,g,b); }

	col(0, 1,10.,10);
    col(1, 1,10.,10);
    col(2, 1,10.,10);
    col(3, 1,10.,10);
    col(4, 6,.1,.1);
    col(5, 6,.1,.1);
    col(6, 6,.1,.1);
    col(7, 6,.1,.1);

	color *= 0.2;
	fragColor = vec4(color,1.0);
}
