vec2 cSpace;
const float pi = 3.14159;

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec3 col;
  vec2 canvas, uv, duv;
  float tCur, a, s, t, c, f;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  PxInit ();
  t = mod (0.15 * tCur, 6.);
  s = 1. - 2. * step (3., t);
  a = 2. * pi * clamp ((mod (t, 1.) - 0.1) / 0.8, 0., 1.);
  col = HsvToRgb (vec3 (mod (0.4 + t + uv.x, 1.), 1., 1.));
  t = floor (mod (t, 3.));
  if (t == 0.) {
    f = 55.;
  } else if (t == 1.) {
    f = 60.;
    col = vec3 (1., 0., 0.);
  } else if (t == 2.) {
    f = 60.;
    col = vec3 (0., 1., 0.);
  }
  uv = f * Rot2D (uv, 2. * a * s) * (1.5 - 0.5 * cos (a)) *
     (1. - (1. -  canvas.y / canvas.x) * cos (4. * a));
  uv.x = s * uv.x - 0.2 * uv.y;
  duv = 100. / canvas;
  c = 2. * (step (0.001, PxText (uv, t)) +
     step (0.001, PxText (uv + vec2 (duv.x, 0.), t)) +
     step (0.001, PxText (uv + vec2 (- duv.x, 0.), t)) +
     step (0.001, PxText (uv + vec2 (0., duv.y), t)) +
     step (0.001, PxText (uv + vec2 (0., - duv.y), t))) / 6.;
  col *= c;
  fragColor = vec4 (sqrt (clamp (col, 0., 1.)), 1.);
}

