float PxChar (vec2 p, vec4 c)
{
  vec2 cb;
  float pOn, b;
  p = floor (p - cPos);
  if (min (p.x, p.y) >= 0. && max (p.x - 8., p.y - 12.) < 0.) {
    b = 8. * (p.y + 1.) - (p.x + 1.);
    if (b < 48.) cb = (b < 24.) ? vec2 (c.w, b) : vec2 (c.z, b - 24.);
    else cb = (b < 72.) ? vec2 (c.y, b - 48.) : vec2 (c.x, b - 72.);
    pOn = mod (floor (cb.x / exp2 (cb.y)), 2.);
  } else pOn = 0.;
  cPos.x += cSpace.x;
  return pOn;
}

void PxBgn (vec2 p)
{
  cPos = floor (-0.5 * p * cSpace);
}

