#define EPS 0.001
#define FAR 100.0
#define STEPS 1028
#define N 1

const float PI = 3.14159265359;


float hash( float n )
{

    return fract( sin( n ) * 45843.349 );

}

float map( vec3 p )
{

    for( int i = 0; i < N; ++i )
    {

        if( p.x * p.x + p.y * p.y + p.z * p.z < 8.0 )
        {
            vec4 mou = iMouse / iResolution.xyxy;
            vec3 grow = vec3( 1.0 );

        	p.xyz += sin( 2.0 * p.yzx * mou.x ) * grow.x;
        	p.xyz += cos( 2.0 * p.yzx * mou.y ) * grow.y;
            p.xyz += cos( 4.0 * p.yzx * mou.z ) * grow.z;
            p.xyz += sin( 4.0 * p.yzx * mou.x ) * grow.x;
            p.xyz += sin( 6.0 * p.yzx * mou.y ) * grow.y;
            p.xyz += sin( 6.0 * p.yzx * mou.z ) * grow.z;
        }
    }

    float t = p.y + 1.0;

    float u = length( p ) - 1.0;

    if( u < t ) t = u;

    return t;

}

vec3 grad( vec3 p )
{

    vec2 e = vec2( 0.0, EPS );
    vec3 n = vec3( map( p + e.xyy ) - map( p - e.xyy ),
                   map( p + e.yxy ) - map( p - e.yxy ),
                   map( p + e.yyx ) - map( p - e.yyx )
    				);
    return n;

}

float softShadows( in vec3 ro, in vec3 rd )
{

    float res = 1.0;
    for( float t = 0.1; t < 8.0; ++t )
    {

        float h = map( ro + rd * t );

        if( h < 0.001 ) return 0.0;
        res = min( res, 8.0 * h / t );
        t += h;

    }

    return res;

}


vec3 randomSphereDir(vec2 rnd)
{
	float s = rnd.x*PI*2.;
	float t = rnd.y*2.-1.;
	return vec3(sin(s), cos(s), t) / sqrt(1.0 + t * t);
}

vec3 randomHemisphereDir(vec3 dir, float i)
{
	vec3 v = randomSphereDir( vec2(hash(i+1.), hash(i+2.)) );
	return v * sign(dot(v, dir));
}

float ambientOcclusion( in vec3 p, in vec3 n, in float maxDist, in float falloff )
{
	const int nbIte = 32;
    const float nbIteInv = 1./float(nbIte);
    const float rad = 1.-1.*nbIteInv; //Hemispherical factor (self occlusion correction)

	float ao = 0.0;

    for( int i=0; i<nbIte; i++ )
    {
        float l = hash(float(i))*maxDist;
        vec3 rd = normalize(n+randomHemisphereDir(n, l )*rad)*l; // mix direction with the normal
        													    // for self occlusion problems!

        ao += (l - max(map( p + rd ),0.)) / maxDist * falloff;
    }

    return clamp( 1.-ao*nbIteInv, 0., 1.);
}


vec3 shade( vec3 ro, vec3 rd, float t )
{

    vec3 pos = ro + rd * t;
    vec3 n = normalize( grad( pos ) );

    vec3 lig = normalize( vec3( 1.0, 0.8, 0.6 ) );
    vec3 blig = vec3( -lig.x, -lig.y, -lig.z );
    vec3 ref = reflect( rd, n );

    float a = ambientOcclusion( pos, n, 4.0, 2.0 );
    float dif = max( 0.0, dot( n, lig ) );
    float bac = max( 0.0, 0.2 + 0.8 * dot( n, blig ) );
    float spe = pow( clamp( dot( lig, ref ), 0.0, 1.0 ), 16.0 ); 
    float con = 1.0;
    float sha = softShadows( pos, lig );
    float amb = 0.5 + 0.5 * n.y;

    vec3 col = con * vec3( 0.1, 0.15, 0.2 );
    col += amb * vec3( 0.1, 0.15, 0.2 );
    col += dif * vec3( 1.0, 0.97, 0.85 ) * sha;
    col += bac * vec3( 1.0, 0.97, 0.85 );

    col = sqrt( col );
    col *= 0.6;
    col += 1.0 * spe;
    col *= a;

    vec3 tex = texture( iChannel0, pos.xz ).xyz;

    if( pos.y > 0.01 )
    {

        col *= tex;

    }

    else
    {
        vec3 texO = texture( iChannel1, pos.xz ).xyz;
        col *= texO;
    }

    return col;
}


vec3 camera(vec3 ro, vec2 uv, vec3 ta) {
	vec3 fwd = normalize(ta - ro);
	vec3 left = cross(vec3(0, 1, 0), fwd);
	vec3 up = cross(fwd, left);
	return normalize(fwd + uv.x*left + up*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( -iResolution.xy + 2.0 * fragCoord ) / iResolution.xy;
	uv.x *= iResolution.x / iResolution.y;

    float dt = iTime * .1;
    float x = 4.0;
	vec3 ro = vec3( x * cos( dt ), 1.0, -x * sin( dt ) );
    vec3 ta = vec3(0, 0, 0);
	vec3 rd;
    rd = camera(ro, uv, ta);

    float t = 0.0; float d = EPS;
    for( int i = 0; i < STEPS; ++i )
    {

        d = EPS * map( ro + rd * t );
        if( d < EPS || t > FAR ) break;

        t += d;

    }

    // Time varying pixel color
    vec3 col = d < EPS ? shade( ro, rd, t ) : mix( vec3( 0.5 ), vec3( 0.0 ), uv.y );

    // Output to screen
    fragColor = vec4(col,1.0);
}
